INSERT INTO `berita` (`id_berita`, `judul`, `id_kategori`, `gambar`, `isi_berita`, `tgl_posting`, `id_user`, `dilihat`) VALUES
(2, 'Percepatan Pembangunan Kawasan Industri Jadi Berita Terpopuler Harian Ekonomi', 2, 'industri.jpg', '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Jakarta: Percepatan pembangunan kawasan industri menjadi berita terpopuler&nbsp;</span><span style=\"font-size:12.0pt\">kanal ekonomi Menit.com kemarin. Berikut rangkuman lima berita terpopuler lainnya di bawah ini.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>1. Kemenperin Percepat Pembangunan Kawasan Industri Halal Demi Tarik Investasi</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"Kemenperin Percepat Pembangunan Kawasan Industri Halal Demi Tarik Investasi\" src=\"https://cdn.medcom.id/dynamic/content/2021/11/27/1358030/jTkfsibXLH.jpg?w=480\" style=\"height:267px; margin:20px 112px; width:400px\" /></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Kementerian Perindustrian (Kemenperin) fokus mendorong tumbuhnya kawasan industri halal melalui penerbitan Peraturan Menteri Perindustrian Nomor 17 Tahun 2020 tentang Tata Cara Memperoleh Surat Keterangan Pembentukan Kawasan Industri Halal.<br />\r\n&nbsp;<br />\r\n&quot;Melalui Permenperin 17/2020, telah mendongkrak percepatan pembangunan kawasan industri halal di Indonesia,&quot; kata Direktur Jenderal Ketahanan, Perwilayahan, dan Akses Industri Internasional (KPAII) Kemenperin Eko S.A. Cahyanto dalam siaran persnya, Sabtu, 27 November 2021.</span></span></span><br />\r\n<span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\"><strong>Berikut tiga kawasan industri halal:</strong></span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Halal Modern Valley di Serang, Banten.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Halal Industrial Park di Sidoarjo.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Bintan Inti Halal Hub.</span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Lebih lanjut ia menjelaskan, dalam upaya membangun kawasan industri halal, pengelola wajib memiliki&nbsp;<em>masterplan</em>&nbsp;yang mencakup perencanaan untuk mendukung industri halal sebagai tenant-nya. Selain itu, memiliki keunggulan karena wajib memiliki Izin Usaha Kawasan Industri (IUKI).</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">&quot;Kawasan industri halal juga perlu dilengkapi dengan fasilitas pendukung berupa tim manajemen halal, sistem manajemen halal, laboratorium halal, lembaga pemeriksa halal, dan instalasi pengolahan air baku&nbsp;tersertifikasi halal. Fasilitas-fasilitas ini yang akan mendorong peningkatan daya saing kawasan industri&nbsp;halal di Indonesia,&quot; papar dia.<br />\r\n&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Di samping itu, dalam mengakselerasi pengembangan industri halal di Indonesia, dibutuhkan dukungan&nbsp;insentif berupa fiskal dan pembiayaan. Kemenperin akan mengusulkan insentif bagi industri halal yang&nbsp;melakukan ekspor, substitusi impor, mengembangkan teknologi proses produk halal, melakukan inovasi&nbsp;industri halal, serta melakukan pembinaan dan pendampingan ekspor bagi pelaku IKM halal. Fasilitas&nbsp;fiskal ini diberikan kepada perusahaan yang berlokasi di kawasan industri halal.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Berikutnya, fasilitas pembiayaan diberikan untuk pendampingan proses produk halal bagi pelaku IKM,&nbsp;sertifikasi halal, inovasi bahan halal pengganti bahan kritis, serta peningkatan&nbsp;<em>bankability</em>&nbsp;bagi IKM yang&nbsp;meliputi pembentukan lembaga, penyelenggaraan, pendampingan untuk peningkatan&nbsp;<em>credit rating</em>&nbsp;IKM&nbsp;halal.<br />\r\n<br />\r\n&quot;Guna mencapai sasaran tersebut, kami mengajak peran serta seluruh pemangku kepentingan untuk&nbsp;bersama-sama mendorong pengembangan kawasan industri halal dan industri halal di Indonesia,&nbsp;termasuk mengakselerasi kebijakan insentif fiskal, sistem rating, pembiayaan inovasi, dan dukungan&nbsp;anggaran sehingga cita-cita kita bersama untuk menjadikan Indonesia sebagai top produsen industri halal dunia dapat tercapai,&quot; harap Eko.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>2. Harus Ada Standardisasi Halal Secara Global</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"Rachmat Gobel: Harus Ada Standardisasi Halal Secara Global\" src=\"https://cdn.medcom.id/dynamic/content/2021/11/27/1358033/6Mky12sjoz.jpg?w=480\" style=\"height:267px; margin:20px 112px; width:400px\" /><br />\r\n<span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Wakil Ketua DPR RI Rachmat Gobel menyatakan perlu ada kesamaan dalam standardisasi halal internasional. Hal Ini penting demi meningkatkan daya saing dan rasa percaya terhadap label produk halal di seluruh dunia.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Gobel mengatakan negara-negara berpenduduk mayoritas muslim dan lembaga pemberi sertifikat halal di seluruh dunia harus dapat membangun kesamaan prosedur, ukuran, dan metode pengujian halal.<br />\r\n&nbsp;<br />\r\n&quot;Harus diakui saat ini masih ada perbedaan-perbedaan di antara negara-negara atau lembaga-lembaga pemberi sertifikat halal,&quot; ungkap Gobel saat mengikuti World Halal Expo 2021 dan World Halal Summit di Istanbul, Turki, dikutip dari siaran persnya, Sabtu, 27 November 2021.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Menurut Gobel, sebagai negara dengan penduduk muslim terbesar di dunia, Indonesia harus banyak belajar dari Turki. Negara itu mampu membangun industri halal, terutama Usaha Mikro Kecil Menengah (UMKM) dengan kemampuan teknologinya.<br />\r\n<br />\r\n&quot;Banyak negara Islam lainnya yang memproduksi produk halal, namun Turki merupakan negara dengan mayoritas muslim yang mampu membangun industri bukan sekadar membangun pabrik, sehingga struktur industri, terutama industri &nbsp;halalnya, kuat,&quot; tutur dia.<br />\r\n<br />\r\nUntuk itu, guna mengembangkan industri halal di dalam negeri, Indonesia perlu bekerja sama dengan Turki. Sebab keberhasilan Turki mengembangkan industri dan produk halal juga tidak lepas dari kemampuan negara itu membangun pariwisatanya, terutama pariwisata religi, dimana banyak peninggalan kejayaan Islam Turki Usmani.<br />\r\n<br />\r\n&quot;Kami berharap kunjungan kerja kali ini akan bermanfaat untuk mengembangkan industri dan produk halal di Indonesia dan memperkuat kerja sama produk halal dengan negara muslim lainnya di dunia,&quot; harap Gobel.<br />\r\n<br />\r\nSementara itu, Wakil Ketua Komisi XI DPR M Amir Uskara mengatakan bahwa pemerintah perlu memberi dukungan lebih serius pada pengembangan produk halal di Indonesia, baik berupa insentif langsung maupun stimulus kepada UMKM untuk membangun produk halal.<br />\r\n<br />\r\nHal senada juga dikemukakan anggota Komisi VI DPR Charles Meikyansyah. Ia menuturkan, pengembangan produk halal harus dibangun dalam bingkai ekosistem ekonomi syariah yang jelas.<br />\r\n<br />\r\n&quot;Hal tersebut agar bisa tumbuh optimal, di samping mempersiapkan sumber daya manusia hingga teknologi dan dukungan pemerintah,&quot; tegasnya.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>3. Sri Mulyani Ingin Ekonomi Papua Tumbuh Lebih Tinggi</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"Sri Mulyani Ingin Ekonomi Papua Tumbuh Lebih Tinggi\" src=\"https://cdn.medcom.id/dynamic/content/2021/11/27/1358034/g36Wzw2oGh.jpg?w=480\" style=\"height:298px; margin:20px 112px; width:400px\" /><br />\r\n<span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Menteri Keuangan (Menkeu) Sri Mulyani Indrawati ingin ekonomi Papua tumbuh lebih tinggi. Pada tahun ini ekonomi di wilayah tersebut tumbuh menjulang di atas rata-rata nasional yakni 14 persen lantaran didorong oleh ekspor komoditas.<br />\r\n&nbsp;<br />\r\n&quot;Dalam situasi ini nanti Papua akan menikmati, selain dana otsus (otonomi khusus), nanti akan dapat dana bagi hasil. Apalagi kemarin kita sudah mendukung sepenuhnya penyelenggaraan PON (Pekan Olahraga Nasional) yang sukses dan Peparnas (Pekan Paralimpiade Nasional),&quot; ujar Sri Mulyani dikutip dari siaran persnya, Sabtu, 27 November 2021.<br />\r\n&nbsp;Ani, sapaannya, juga mengharapkan hal tersebut menjadi momentum bagi Papua untuk terus membangun sumber dayanya menjadi manusia-manusia yang hebat.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">&quot;Kita akan dukung sepenuhnya, kita berharap seluruh anggaran yang sudah dialokasikan dimanfaatkan untuk semaksimal mungkin menciptakan perbaikan bagi SDM dan kesejahteraan, serta mendorong perekonomian untuk bangkit kembali,&quot; tegasnya.<br />\r\n<br />\r\nIa menekankan bahwa Kementerian Keuangan (Kemenkeu) terus mendukung percepatan pembangunan, kesejahteraan, peningkatan kualitas pelayanan publik, serta kesinambungan dan keberlanjutan pembangunan di wilayah Papua.<br />\r\n&quot;Untuk mendukung kemajuan Papua, Kemenkeu mendorong percepatan melalui pembangunan infrastruktur, pembangunan SDM Unggul, dan dukungan pengelolaan Dana Otonomi Khusus,&quot; jelas dia.<br />\r\n<strong>Peningkatan kualitas SDM</strong><br />\r\nDi sisi lain, Kemenkeu memberikan dukungan yang sangat besar kepada peningkatan kualitas Sumber Daya Manusia (SDM) Unggul yang dilakukan melalui program beasiswa afirmasi Lembaga Pengelola Dana Pendidikan (LPDP) untuk mencetak alumni yang mampu memberdayakan masyarakat Papua.<br />\r\n<br />\r\nKontribusi Kemenkeu melalui LPDP dalam peningkatan SDM di Provinsi Papua dan Papua Barat dari 2013 hingga 2021 mampu melahirkan sebanyak 493 lulusan dalam dan luar negeri yang terdiri dari 328 magister, 160 doktor, serta lima dokter spesialis.<br />\r\n<br />\r\nSementara itu, perbaikan kebijakan Dana Otsus bertujuan untuk kemajuan dan peningkatan kesejahteraan bagi rakyat Papua dan orang asli Papua. Perubahan besaran Dana Otsus dari 2 persen menjadi 2,25 persen dari alokasi DAU diharapkan dapat memberikan manfaat secara optimal, adil, dan akuntabel bagi percepatan pembangunan Papua.<br />\r\n<br />\r\n&quot;Selain itu, dukungan Kemenkeu juga dilakukan terhadap perbaikan tata kelola baru dalam penggunaan Dana Otsus untuk perbaikan pengelolaan penggunaan Dana Otonomi Khusus menuju masyarakat Papua dan orang asli Papua yang lebih sejahtera,&quot; harap Ani.<br />\r\n<br />\r\nKe depan, dukungan percepatan pengelolaan Dana Otsus melalui Sekretariat Badan Pengarah Percepatan Pembangunan Otonomi Khusus (BP3OKP) yang dilaksanakan oleh Kantor Vertikal Kemenkeu memiliki peran sinkronisasi, harmonisasi, evaluasi, dan koordinasi untuk percepatan pembangunan Papua.<br />\r\n<br />\r\nHadirnya asistensi dan supervisi Pemerintah Pusat (melalui K/L teknis) sejak tahap perencanaan, pembinaan, pengawasan, sampai dengan evaluasi akan memastikan penyaluran dana otsus dan TKDD di Papua dapat berjalan dengan baik.<br />\r\n<br />\r\n&quot;Diharapkan dengan memangkas rantai birokrasi dan pelaksanaan pendampingan dan pengawasan yang komprehensif misi mulia untuk memberikan pemerataan pembangunan dan peningkatan kesejahteraan di Papua dapat segera dicapai,&quot; pungkas Ani.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"font-size:18px\"><strong>4. Waduh Kabur Lagi! Dana Asing Hilang Rp1,29 Triliun dalam Sepekan</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"Waduh Kabur Lagi! Dana Asing Hilang Rp1,29 Triliun dalam Sepekan\" src=\"https://cdn.medcom.id/dynamic/content/2021/11/27/1357954/m6Rc4PtOZp.PNG?w=480\" style=\"height:240px; margin:20px 112px; width:400px\" /></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Bank Indonesia (BI) mencatat dana-dana asing keluar dari pasar keuangan domestik selama sepekan terakhir. Berdasarkan data transaksi pada 22-25 November 2021, dana dari investor asing (nonresiden) tersebut tercatat jual neto (outflow) sebanyak Rp1,29 triliun.<br />\r\n&nbsp;<br />\r\nMinggatnya dana asing dari pasar keuangan domestik tersebut berasal dari pasar Surat Berharga Negara (SBN) sebanyak Rp1,77 triliun. Beruntung di pasar saham dana-dana mengalir (beli neto) ke pasar domestik sebesar Rp480 miliar.<br />\r\n&nbsp;<br />\r\nBerdasarkan data setelmen selama 2021 (<em>year to date</em>/ytd), nonresiden jual neto Rp18,59 triliun,&quot; ungkap Kepala Departemen Komunikasi BI Erwin Haryono dikutip dari rilis perkembangan indikator stabilitas nilai rupiah, Sabtu, 27 November 2021.<br />\r\n<br />\r\nAdapun premi risiko atau&nbsp;<em>Credit Default Swap</em>&nbsp;(CDS) Indonesia lima tahun naik ke level 79,88 basis poin (bps) per 25 November 2021 dari 76,96 bps per 19 November 2021. CDS merupakan indikator untuk mengetahui risiko berinvestasi di SBN.<br />\r\n<br />\r\nSemakin besar skor CDS, maka risiko berinvestasi di SBN juga semakin tinggi. Sebaliknya jika skor semakin kecil, maka risiko investasinya juga semakin rendah.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><strong><em><span style=\"font-size:12.0pt\">Capital outflow</span></em></strong><strong><span style=\"font-size:12.0pt\">&nbsp;tekan kurs rupiah</span></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Minggatnya aliran modal asing dari pasar keuangan domestik turut menyeret nilai tukar rupiah yang melempem di akhir pekan. Mata uang Garuda tersebut dipaksa bertekuk lutut dihadapan dolar Amerika Serikat (AS).<br />\r\n<br />\r\nMengutip data&nbsp;<em>Bloomberg&nbsp;</em>pada penutupan perdagangan akhir pekan ini, nilai tukar rupiah terhadap USD melemah 70 poin atau setara 0,49 persen, dari level Rp14.287 per USD menjadi Rp14.357 per USD.<br />\r\n<br />\r\nSementara menukil data&nbsp;<em>Yahoo Finance</em>, rupiah juga berada di zona merah pada posisi Rp14.275 per USD. Rupiah melemah tipis tujuh poin atau setara 0,05 persen dari Rp14.268 per USD di penutupan perdagangan hari sebelumnya.<br />\r\n<br />\r\nSedangkan berdasar pada data kurs referensi Jakarta Interbank Spot Dolar Rate (Jisdor), rupiah diperdagangkan stagnan di level Rp14.280 per USD.<br />\r\n<br />\r\nTerkait hal tersebut, Erwin menekankan bahwa Bank Indonesia akan terus memperkuat koordinasi dengan pemerintah dan otoritas terkait untuk memonitor secara cermat dinamika penyebaran covid-19 dan dampaknya terhadap perekonomian Indonesia dari waktu ke waktu.<br />\r\n<br />\r\n&quot;Termasuk langkah-langkah koordinasi kebijakan lanjutan yang perlu ditempuh untuk menjaga stabilitas makroekonomi dan sistem keuangan, serta menopang pertumbuhan ekonomi Indonesia tetap baik dan berdaya tahan,&quot; tutup Erwin.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">&nbsp;<br />\r\n&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>5. Naik Lagi, Inflasi November 2021 Diperkirakan Capai 0,34%</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"Naik Lagi, Inflasi November 2021 Diperkirakan Capai 0,34%\" src=\"https://cdn.medcom.id/dynamic/content/2021/11/27/1357921/9oSI1WxuYC.jpg?w=480\" style=\"height:267px; margin:20px 112px; width:400px\" /><br />\r\n<span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Bank Indonesia (BI) kembali menaikkan proyeksi inflasi November 2021 menjadi 0,34 persen, sesuai hasil Survei Pemantauan Harga (SPH) hingga pekan keempat. Pada hasil SPH di pekan ketiga, bank sentral memproyeksi inflasi bulan ini sebesar 0,31 persen (mtm).<br />\r\n&nbsp;<br />\r\n&quot;Berdasarkan Survei Pemantauan Harga pada pekan keempat November 2021, perkembangan harga pada November 2021 tetap terkendali dan diperkirakan inflasi sebesar 0,34 persen (mtm),&quot; ungkap Kepala Departemen Komunikasi BI Erwin Haryono dikutip dari rilis perkembangan indikator stabilitas nilai rupiah, Sabtu, 27 November 2021.<br />\r\n&nbsp;<br />\r\nDengan perkembangan tersebut, jelas Erwin, perkiraan inflasi November 2021 secara tahun kalender sebesar 1,27 persen (ytd). Sementara inflasi secara tahunan sebesar 1,72 persen (yoy).</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><strong><span style=\"font-size:12.0pt\">Penyumbang utama inflasi November 2021:</span></strong></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Komoditas telur ayam ras sebesar 0,10 persen (mtm)</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Minyak goreng sebesar 0,08 persen (mtm)</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Cabai merah sebesar 0,06 persen (mtm)</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Emas perhiasan sebesar 0,02 persen (mtm)</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Sawi hijau, bayam, daging ayam ras, sabun detergen bubuk, angkutan udara, dan rokok kretek filter masing-masing sebesar 0,01 persen (mtm)</span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">&quot;Sementara itu, beberapa komoditas mengalami deflasi antara lain bawang merah dan tomat masing masing sebesar 0,02 persen (mtm) dan 0,01 persen (mtm),&quot; urainya.<br />\r\n<br />\r\nTerkait hal tersebut, Erwin menekankan bahwa Bank Indonesia akan terus memperkuat koordinasi dengan Pemerintah dan otoritas terkait untuk memonitor secara cermat dinamika penyebaran covid-19 dan dampaknya terhadap perekonomian Indonesia dari waktu ke waktu.<br />\r\n<br />\r\n&quot;Termasuk langkah-langkah koordinasi kebijakan lanjutan yang perlu ditempuh untuk menjaga stabilitas makroekonomi dan sistem keuangan, serta menopang pertumbuhan ekonomi Indonesia tetap baik dan berdaya tahan,&quot; tegas Erwin.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><strong><span style=\"font-size:12.0pt\">Inflasi Oktober dipengaruhi kelompok&nbsp;<em>volatile food</em></span></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Badan Pusat Statistik (BPS) sebelumnya mencatat IHK Oktober 2021 mengalami inflasi sebesar 0,12 persen (mtm), setelah pada bulan sebelumnya mencatat deflasi 0,04 persen (mtm). Perkembangan ini dipengaruhi oleh peningkatan inflasi kelompok&nbsp;<em>volatile food</em>&nbsp;dan kelompok&nbsp;<em>administered prices</em>, di tengah penurunan inflasi kelompok inti.<br />\r\n<br />\r\nSecara tahunan, inflasi IHK Oktober 2021 tercatat sebesar 1,66 persen (yoy), lebih tinggi dibandingkan dengan inflasi bulan sebelumnya sebesar 1,60 persen (yoy). Sementara itu, inflasi tahun kalender 2021 pada Oktober sebesar 0,93 persen (ytd).</span></span></span></p>\r\n', '2021-12-17', 3, 8),
(3, 'Airlangga Ungkap Dampak Implementasi UU Ciptaker Bantu Ekonomi RI', 2, '2.jpeg', '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Jakarta - Menteri Koordinator Bidang Perekonomian Airlangga Hartarto mengungkapkan dampak positif atas implementasi Undang-undang Cipta Kerja yang telah disahkan dalam rapat paripurna DPR tanggal 5 Oktober 2020 lalu. Salah satu di antaranya UU Cipta Kerja dapat meningkatkan investasi, lapangan kerja serta perizinan berusaha yang dikeluarkan oleh Online Single Submission (OSS).</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&quot;BKPM telah mencatat kenaikan realisasi investasi pada tahun 2021 sebesar 7,8% (YoY Januari-September) dengan nilai investasi sebesar Rp 659 triliun,&quot; ujar Menteri Kordinator Bidang Perekonomian dalam keterangan tertulis, Selasa (30/11/2021).<br />\r\n<br />\r\nMenurutnya, kenaikan investasi tersebut terbilang cukup besar untuk membantu perekonomian Indonesia di tengah pandemi COVID-19 saat ini. Selain itu implementasi UU Ciptaker juga meningkatkan lapangan pekerjaan.<br />\r\n<br />\r\nTercatat jumlah penciptaan kesempatan kerja baru pada Triwulan 1 sampai dengan 3 di tahun 2021 sebanyak 912.402 tenaga kerja. Pada Triwulan 1 sebanyak 311.793 tenaga kerja, di Triwulan 2 sebanyak 311.922 tenaga kerja, dan Triwulan 3 sebanyak 288.687 tenaga kerja.<br />\r\n<br />\r\nTercatat jumlah penciptaan kesempatan kerja baru pada Triwulan 1 sampai dengan 3 di tahun 2021 sebanyak 912.402 tenaga kerja. Pada Triwulan 1 sebanyak 311.793 tenaga kerja, di Triwulan 2 sebanyak 311.922 tenaga kerja, dan Triwulan 3 sebanyak 288.687 tenaga kerja.<br />\r\n<br />\r\nKemudian implementasi lainnya yang juga berdampak positif terlihat pada perizinan berusaha yang dikeluarkan oleh Online Single Submission (OSS). Pada periode 4 Agustus sampai dengan 31 Oktober, OSS juga telah menerbitkan 379.051 perizinan berusaha. Dominan perizinan berusaha diberikan kepada usaha mikro yakni sebanyak 357.893 perizinan atau 94,42%, lalu kedua pada usaha kecil sebanyak 14.818 perizinan atau 3,91%, kemudian usaha menengah sebanyak 3.783 perizinan atau 1%, dan terakhir pada usaha besar sebanyak 2.557 perizinan atau 0,67%.<br />\r\n<br />\r\n&quot;OSS tetap berjalan dengan baik untuk melayani perizinan berusaha yang baru maupun yang mengajukan perpanjangan,&quot; ujar Airlangga.</span></span></span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Meski begitu, undang-undang sapu jagat itu telah diputuskan untuk diperbaiki oleh Mahkamah Institusi (MK). Perbaikan ini diminta untuk dilakukan selama 2 tahun ke depan, bila tidak pasal-pasal yang ada di dalam UU Cipta Kerja tidak berlaku.<br />\r\n<br />\r\nPemerintah dan DPR RI akan segera melakukan revisi UU Cipta Kerja pasca putusan MK. Airlangga mengatakan revisi UU Cipta Kerja ini akan masuk dalam Program Legislasi Nasional DPR di tahun 2022.<br />\r\n<br />\r\n&quot;Pemerintah bersama DPR RI akan revisi UU Cipta Kerja dan UU Pembentukan Peraturan undang-undang dalam rangka harmonisasi UU Cipta Kerja ke depan paska keputusan MK,&quot; ungkap Airlangga dalam konferensi pers virtual, Senin (29/11/2021).</span></span></p>\r\n', '2021-12-17', 3, 5),
(4, 'Ekonomi Indonesia Kuartal III 2021 Tumbuh 3,51 Persen', 2, '3.jpeg', '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">JAKARTA, Menit.com - Badan Pusat Statistik (BPS) melaporkan pertumbuhan ekonomi Indonesia pada kuartal III-2021 sebesar 3,51 persen (year on year/yoy). Realisasi itu lebih rendah dari pertumbuhan kuartal II-2021 yang mencapai 7,07 persen (yoy). </span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Meski demikian, posisi ini membaik bila dibandingkan dengan laju ekonomi Indonesia kuartal III-2020 yang minus 3,49 persen. </span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&quot;Bila dibandingkan dengan kuartal III-2020 maka pertumbuhan ekonomi di kuartal III-2021 sebesar 3,51 persen,&quot; ujar Kepala BPS Margo Yuwono dalam konferensi pers virtual, Jumat (5/11/2021).</span></span></span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Dia mengatakan, pertumbuhan ekonomi Indonesia sejalan dengan pertumbuhan eonomi mitra dagang utama yang tercatat positif di kuartal III-2021, meski lebih rendah dari kuartal sebelumnya. </span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Seperti China ekonominya tumbuh 4,9 persen, Amerika Serikat (AS) tumbuh 4,9 persen, Singapura tumbuh 6,5 persen, Korea Selatan tumbuh 4 persen, Hong Kong tumbuh 5,4 persen, dan Uni Eropa tumbuh 3,9 persen. </span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Selain itu, pertumbuhan ekonomi kuartal III-2021 juga dipengaruhi harga sejumlah komoditas yang terus meningkat, baik makanan maupun tambang. Di antaranya minyak kelapa sawit, cokelat, kopi, timah, nikel, dan alumunium<br />\r\n<br />\r\n&quot;Perkembangan harga komoditas dan membaiknya perekonomian mitra dagang kita ini berpengaruh besar pada kinerja ekspor, yang juga mempengaruhi perekonomian,&quot; kata dia. </span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Selain itu, rendahnya mobilitas masyarakat di sepanjang Juni-September 2021 seiring dengan penerapan PPKM Darurat atau Level 4 juga turut berpengaruh. Pemerintah memperketat mobilitas masyarakat untuk menekan penyebaran Covid-19 yang melonjak di Indonesia. </span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&quot;Secara keseluruhan di kuartal III-2021 mobilitas penduduk lebih rendah dibandingkan kuartal II-2021 maupuan kuartal III-2020,&quot; imbuh Margo. </span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Berdasarkan berbagai indikator di global maupun domestik tersebut, ekonomi Indonesia berdasarkan Produk Domestik Bruto (PDB) di kuartal III-2021 atas dasar harga berlaku sebesar Rp 4.325,4 triliun. Sementara berdasarkan harga dasar konstan dengan tahun dasar 2010 sebesar Rp 2.815 triliun. </span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Adapun secara kumulatif Januari-September 2021, pertumbuhan ekonomi Indonesia sebesar 3,24 persen dibandingkan periode yang sama di 2020.</span></span><br />\r\n&nbsp;</p>\r\n', '2021-12-17', 3, 2),
(5, 'Sri Mulyani: Pembukaan Kembali Ekonomi, Pendapatan Negara Naik 18%', 2, '4.jpg', '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Jakarta, Menit.com</span></span></strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;- Menteri Keuangan Sri Mulyani Indrawati</span></span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;menyebutkan pembukaan kembali ekonomi telah mendorong geliat aktivitas masyarakat sehingga turut memicu pendapatan negara yang terus meningkat. Hal ini tercermin dari realisasi pendapatan negara hingga akhir Oktober 2021 mencapai Rp 1.510,0 triliun atau 86,6% dari target dalam pagu APBN sebesar Rp 1.743,6 triliun.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Ia mengatakan pendapatan negara tumbuh 18,2% (yoy), dibandingkan periode yang sama tahun lalu sebesar Rp 1.277 triliun.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&ldquo;Tahun 2021 seiring pemulihan ekonomi dan&nbsp;<em>reopening</em>&nbsp;mendorong aktivitas masyarakat dan ekonomi mulai bergerak kembali, maka APBN dari sisi pendapatan negara (Oktober) kita alami&nbsp;<em>growth double digit</em>&nbsp;hingga 18,2%, itu&nbsp;<em>recovery</em>&nbsp;dari tahun lalu yang kontraksi 15,3%,&rdquo; tuturnya dalam CEO Networking 2021, Selasa (16/11/2021).</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Secara rinci, pendapatan negara terdiri dari penerimaan pajak Rp 953,6 triliun atau sudah mencapai 77,6% dari target Rp 1.229,6 triliun dan meningkat 15,3% secara yoy dibandingkan realisasi periode yang sama tahun lalu sebesar Rp 69 triliun dan mengalami kontrakasi 18,8%.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Kemudian penerimaan bea cukai hingga Oktober tercatat meningkat signikan hingga 25,5% yoy atau Rp 205,8 triliun. Realisasi ini bahkan sudah mencapai 95,7% dari pagu Rp 215 triliun.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&ldquo;Realisasi ini gambarkan APBN mulai pulih saat ekonomi pulih, maka dua tugas yang harus dilakukan bersama sama bukan pilihan tapi harus sama sama dilakukan yakni belanja coba dikendalikan tanpa mengorbankan sisi kebutuhan Covid-19, karena belanja untuk covid-19 masih sangat penting,&rdquo; tuturnya.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Selanjutnya sisi penerimaan negara bukan pajak (PNBP) juga tercatat Rp 349,2 triliun atau tumbuh 25,2% yoy. Bahkan capaian PNBP hingga akhir Oktober sudah melampaui target hingga 117,1% yang tercantum dalam pagu sebesar Rp 298,2 triliun.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&ldquo;Peningkatan kinerja PNBP utamanya didukung penerimaan SDA sejalan dengan tren peningkatan harga komoditas&rdquo;tuturnya.</span></span></span></span></p>\r\n', '2021-12-17', 3, 1),
(6, 'Bank Indonesia Perkirakan Pertumbuhan Ekonomi 4,7-5,5 Persen di 2022', 2, '5.jpg', '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">MENIT.COM,&nbsp;Jakarta&nbsp;-Bank Indonesia memperkirakan pertumbuhan ekonomi</span><span style=\"font-size:12.0pt\">&nbsp;Indonesia pada tahun 2022 akan mencapai 4,7 sampai 5,5 persen. Adapun di 2021 adalah di rentang 3,2-4,0 persen.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">&quot;Didorong oleh berlanjutnya perbaikan ekonomi global yang berdampak pada kinerja ekspor yang tetap kuat, serta meningkatnya permintaan domestik dari kenaikan konsumsi dan investasi,&quot; kata Gubernur Bank Indonesia</span><span style=\"font-size:12.0pt\">&nbsp;Perry Warjiyo dalam Pertemuan Tahunan Bank Indonesia yang digelar secara hibrida (daring dan luring), Rabu, 24 November 2021.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Hal ini, kata dia, juga didukung vaksinasi</span><span style=\"font-size:12.0pt\">, pembukaan sektor ekonomi, dan stimulus kebijakan. &ldquo;Sinergi kebijakan yang erat dan kinerja perekonomian tahun 2021 menjadi modal untuk semakin bangkit dan optimis akan pemulihan ekonomi Indonesia yang lebih baik pada tahun 2022.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Dia mengatakan penguatan sinergi dan inovasi ditujukan untuk menciptakan imunitas massal dari pandemi Covid-19 dan pembukaan kembali sektor ekonomi prioritas, mendorong pemulihan ekonomi dalam jangka pendek melalui kebijakan peningkatan permintaan.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">&quot;Serta memperkuat pertumbuhan yang lebih tinggi dalam jangka menengah melalui kebijakan reformasi struktural,&quot; demikian disampaikan Gubernur BI,&quot; ujarnya.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Pada kesempatan itu Presiden Joko Widodo menyampaikan apresiasi atas sinergi dan komunikasi yang terjalin intens dan baik diantara jajaran otoritas, baik antara BI, OJK, LPS, dan Pemerintah, dalam hal ini Kementerian Keuangan. Menurutnya, Hal ini menjadi kunci dalam mengelola dampak varian delta Covid 19 terhadap perekonomian nasional di 2021, sehingga setiap permasalahan kecil dapat terselesaikan.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Ke depan, Presiden juga mendorong untuk dikembangkannya ekonomi hijau dan memperkuat digitalisasi ekonomi, khususnya UMKM.&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Perry mengatakan respons bauran kebijakan BI yang bersinergi dengan kebijakan ekonomi nasional akan terus&nbsp;mengawal perekonomian pada tahun 2022. Inflasi rendah dan terkendali pada sasaran 3&plusmn;1 persen pada 2022, didukung oleh kenaikan kapasitas produksi nasional melalui peningkatan efisiensi dan produktivitas dalam memenuhi kenaikan permintaan agregat di dalam perekonomian.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Defisit transaksi berjalan rendah, pada kisaran 1,1-1,9 persen pada 2022. Stabilitas sistem keuangan terjaga, kecukupan modal tinggi, dan likuiditas melimpah. Dana Pihak Ketiga dan kredit akan tumbuh 7,0-9,0 persen dan 6,0-8,0 persen pada 2022.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,\"><span style=\"font-size:12.0pt\">Ekonomi-keuangan digital, kata dia, juga akan meningkat pesat. Di mana pada tahun 2022, nilai transaksi&nbsp;<em>e-commerce&nbsp;</em>diprakirakan akan mencapai Rp 530 triliun, uang elektronik Rp 337 triliun, dan&nbsp;<em>digital banking</em>&nbsp;lebih Rp 48 ribu triliun.</span></span></span></p>\r\n', '2021-12-17', 3, 3),
(7, 'Wali Kota Medan Tandatangani Berita Acara Serah Terima Bantuan Keuangan Partai Politik', 5, '122.jpeg', '<p style=\"text-align:justify\">Wali Kota Medan Bobby Nasution menyaksikan dan melakukan penandatanganan Berita Acara serah terima Bantuan Keuangan Partai Politik yang memiliki kursi di DPRD Kota Medan hasil Pemilu 2019-2024 di ruang Rapat III Kantor Wali Kota, Senin (29/11). Diharapkan bantuan keuangan yang diberikan ini selain untuk operasional sekretariat Partai politik juga dapat mendorong peran aktif Partai Politik guna melakukan pendidikan politik kepada masyarakat kota Medan.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Selain Bobby Nasution Penandatanganan Berita Acara Serah Terima Bantuan Keuangan Partai Politik ini juga dilakukan Ketua dan Bendahara Partai Politik. Penandatanganan diawali oleh Partai PDI-P, Gerindra, PKS, PAN, Golkar, Nasdem, Demokrat, Hanura, PSI dan PPP. Dikatakan Bobby Nasution, bantuan keuangan ini selain untuk mendorong peran aktif Partai Politik untuk ikut serta dalam penanggulangan penyebaran virus Covid-19 melalui pelaksanaan pendidikan Politik kepada masyarakat maupun anggota Parpol. Disamping itu bantuan ini juga untuk kegiatan operasional sekretariat Parpol yang mendapatkan kursi di DPRD Kota Medan.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&quot;Partai Politik memberikan banyak kontribusi dalam menjalankan demokrasi di Indonesia. Oleh karenanya bantuan keuangan ini diharapkan dapat mendorong kuatnya edukasi politik sehingga dapat menguatkan kapasitas Partai dan kader yang dicalonkan. Selain itu juga bermanfaat untuk pendidikan politik bagi masyarakat,&quot; Kata Bobby Nasution.&nbsp;Bobby Nasution berharap agar terus terjalin kolaborasi dan kesamaan persepsi dalam berdemokrasi antara pemerintah dengan Partai politik. Selain itu diharapkan Politik dapat dijadikan sarana pemersatu bangsa walaupun berbeda partai tetap memiliki tujuan sama yaitu menegakkan NKRI. &quot;Saya juga berharap kendala dalam penyusunan laporan pertanggungjawaban dalam pengelolaan keuangan Parpol dapat diminimalisir dan selesai tepat waktu serta tetap berpedoman pada perundang-undangan yang ada,&quot; Jelas Bobby Nasution.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Sebelumnya, Kaban Kesbangpol Arjuna Sembiring mengungkapkan bahwa dasar hukum bantuan keuangan Parpol ini adalah Permendagri nomor 78 tahun 2020 tentang perubahan atas Permendagri nomor 36 tahun 2018 tentang tata cara perhitungan, penganggaran dalam APBD dan tata tertib Pengajuan, penyaluran dan laporan pertanggungjawaban penggunaan bantuan keuangan Parpol.&nbsp;&quot;Tujuan diselenggarakannya kegiatan ini adalah sebagai sarana silaturahmi sekaligus sosialisasi, edukasi serta transparansi yang dilaksanakan Pemko Medan. Disamping itu juga sebagai sarana pendidikan dan pengetahuan kepada masyarakat mengenai pendidikan politik melalui partai politik masing-masing,&quot; Jelasnya.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Turut hadir dalam Penandatanganan berita acara serah terima bantuan Keuangan Partai Politik ini, Ketua DPRD Medan Hasyim SE, Wakil Ketua DPRD Medan Ihwan Ritonga, Wakil Ketua DPRD Medan Bahrumsyah, Asisten Pemerintahan dan Kesejahteraan Muhammad Sofyan, serta Ketua Partai Politik.</p>\r\n', '2021-12-18', 4, 12),
(8, 'Kongres Seknas-Jokowi Tunjuk Rambun Tjajo Jadi Ketum', 5, 'rido1.jpg', '<p style=\"text-align:justify\">JAKARTA -- Kongres Sekretariat Nasional Jaringan Organisasi Komunitas Warga Indonesia (Seknas-Jokowi) memilih dan menetapkan Rambun Tjajo sebagai Ketua Umum Seknas-Jokowi periode 2021-2026. Kongres yang berlangsung di Hotel Sahid Jakarta, pada akhir pekan kemarin, dihadiri dan dibuka oleh Presiden Joko Widodo (Jokowi).</p>\r\n\r\n<p style=\"text-align:justify\">Dalam sambutannya, Presiden Jokowi mengatakan, Seknas-Jokowi mempunyai arti tersendiri dalam perjalanan karier politiknya. Ia memiliki kenangan yang berkesan karena organisasi relawan yang pertama kali dikunjunginya adalah Seknas-Jokowi.</p>\r\n\r\n<p style=\"text-align:justify\">&quot;Saya ingat betul organisasi relawan yang pertama kali saya kunjungi adalah Seknas Jokowi,&quot; kata Presiden Jokowi dalam pembukaan Kongres Seknas Jokowi di Hotel Sahid, Jakarta.</p>\r\n\r\n<p style=\"text-align:justify\">Selain Presiden Jokowi, hadir juga Ketua Dewan Pembina Seknas-Jokowi sekaligus anggota Wantimpres Sidarto Danoesoebroto, mantan Deputi Kantor Staf Presiden Eko Sulistyo, mantan Duta Besar Indonesia untuk Mesir Helmy Fauzi, anggota Dewan Pakar dan utusan Dewan Perwakilan Wilayah (DPW) se-Indonesia serta para pengurus nasional Seknas-Jokowi. Begitu pula dengan para Ketua Umum relawan pendukung Jokowi lainnya yang turut hadir sebagai undangan dalam kongres tersebut.</p>\r\n\r\n<p style=\"text-align:justify\">Kongres pertama Seknas-Jokowi berlangsung&nbsp; khidmat. Selain membahas situasi nasional untuk membaca peta politik Indonesia dan untuk menentukan sikap Seknas-Jokowi ke depannya, agenda yang tak kalah penting adalah pemilihan Ketua Umum dan Sekretaris Jenderal Seknas-Jokowi serta pengurus harian lainnya.</p>\r\n\r\n<p style=\"text-align:justify\">Pada akhir agenda, yakni Pemilihan Ketua Umum dan Sekretaris Jendral Seknas Jokowi, terpilih secara aklamasi Rambun Tjajo sebagai Ketua Umum periode 2021-2026. Pemilihan dilakukan secara&nbsp;<em>offline</em>&nbsp;dan&nbsp;<em>online</em>&nbsp;oleh pengurus Seknas-Jokowi di seluruh Indonesia dan beberapa perwakilan di luar negeri. Rambun Tjahjo sebelumnya menjabat sebagai Wakil Ketua Umum Seknas-Jokowi.</p>\r\n\r\n<p style=\"text-align:justify\">Dalam sambutan pertamanya sebagai Ketua Umum Seknas-Jokowi, Rambun Tjajo yang merupakan mantan aktivis YLBHI mengatakan, siap untuk meneruskan warisan perjuangan mulia yang sudah dibangun dari awal, dan menjaga marwah Seknas-Jokowi sebagai sebuah pelajaran sejarah bagi generasi selanjutnya.</p>\r\n\r\n<p style=\"text-align:justify\">&quot;Secara personal saya hanya terdorong untuk meneruskan legacy sahabat-sahabat saya yang telah berjuang dan bersusah payah berjuang dalam membangun organisasi ini sebagai sebuah&nbsp;<em>legacy</em>. Secara kolektif, apa yang telah dibangun tidak demikian saja terbuang percuma, tanpa sebuah keberlanjutan sebagai sebuah pelajaran sejarah bagi generasi selanjutnya,&quot; tutur Rambun Tjajo.</p>\r\n\r\n<p style=\"text-align:justify\">Ia menambahkan, sebagaimana organisasi relawan Jokowi lainnya, lahir sebagai hasil pencarian sosok pemimpin &#39;baru&#39; yang mewarisi para pendiri bangsa. Seknas-Jokowi menemukan kesempatan sejarah pada sosok Jokowi dalam menapaki kursi kepemimpinan nasional.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&ldquo;Kausalitas kesejarahan ini tidak bisa dibiarkan atau dinafikan dalam melihat ke arah mana organisasi semacam Seknas-Jokowi akan bergerak. Seknas-Jokowi akan masih dalam perputaran (setidaknya untuk sementara ini) siklus politik elektoral kepresidenan. Setidaknya, fenomena politik elektoral menjadi titik masuk untuk pelibatan kekuatan masyarakat sipil secara lebih aktif dan meluas dalam perpolitikan nasional,&rdquo; terang Rambun Tjajo.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Pada 2024 nanti masa jabatan Presiden Jokowi akan berakhir. Sebagai sebuah organisasi, Seknas-Jokowi akan tetap setia pada garis politik yang diemban oleh pemerintahan nasional di bawah kepemimpinan Jokowi. Seknas-Jokowi akan melihat perkembangan politik ke depan, dan memperhatikan pilihan dari pemimpin politik nasional Jokowi.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&quot;Dan kami akan bersabar dalam menentukan pilihan politik organisasi kami, sekurangnya dalam waktu satu tahun ke depan,&quot; tegas Rambun Tjajo.</p>\r\n', '2021-12-18', 4, 8);
INSERT INTO `berita` (`id_berita`, `judul`, `id_kategori`, `gambar`, `isi_berita`, `tgl_posting`, `id_user`, `dilihat`) VALUES
(9, 'Imparsial dan Komnas HAM Dukung Wacana Penarikan Pasukan Non-Organik Papua', 5, 'rido2.jpg', '<p style=\"text-align:justify\"><strong>Jakarta</strong>&nbsp;- Rencana Panglima TNI Jenderal Andika Perkasa menerapkan pendekatan teritorial memunculkan wacana penarikan pasukan non-organik yang ada di Tanah Papua. Selama ini, keberadaan pasukan ini dianggap menjadi salah satu biang kerok tak kunjung usainya kekerasan di Papua.</p>\r\n\r\n<p style=\"text-align:justify\">Direktur Imparsial Ghufron Mabruri menilai kebijakan pengerahan pasukan non-organik selama ini memiliki banyak masalah. Tidak hanya terkait dengan akar kekerasan, tapi secara kebijakan, hal ini juga bermasalah.</p>\r\n\r\n<p style=\"text-align:justify\">&quot;Tak ada akuntabilitas, kontrol, dan pengawasan. Sehingga, potensi penyimpangan pada operasi pasukan non-organik banyak terjadi di lapangan,&quot; kata Ghufron saat dihubungi Tempo, Jumat, 17 Desember 2021.</p>\r\n\r\n<p style=\"text-align:justify\">Selama ini, ia menilai pelaksanaan operasi militer selain perang (OMSP) di Papua juga belum sesuai dengan ketentuan pasal 7 ayat 2 dan 3 di Undang-Undang Tentara Nasional Indonesia. Seharusnya, pelaksanannya mensyaratkan adanya keputusan politik negara.</p>\r\n\r\n<p style=\"text-align:justify\">Komisioner Komnas HAM Choirul Anam mengatakan, berdasarkan laporan yang mereka selama ini terima, pelaku kekerasan oleh TNI di Papua berasal dari satuan non-organik. Karena itu, Anam mendukung rencana Andika Perkasa untuk memperkuat operasi teritorial dengan menjadikan pasukan organik sebagai tulang punggung.</p>\r\n\r\n<p style=\"text-align:justify\">&quot;Sebagai suatu konsep, (pendekatan teritorial) ini lebih baik daripada pendekatan operasi yang tulang punggungnya pasukan non-organik,&quot; kata Anam.</p>\r\n\r\n<p style=\"text-align:justify\">Pendekatan teritorial ini juga harus diiringi dengan menurunkan stigma pada masyarakat Papua, yang masih kerap terjadi saat pendekatan operasi oleh pasukan non-organik dilakukan. Anam mengatakan stigma ini kerap menyebabkan salah tangkap saat aparat mengejar kelompok kriminal bersenjata.</p>\r\n\r\n<p style=\"text-align:justify\">Ia mencontohkan warga sipil yang salah ditangkap karena diduga terlibat KKB. Padahal dasar penangkapan hanya berdasarkan ciri fisik dan tanpa data identitas yang lengkap. Belakangan, bahkan kelompok kebebasan Papua juga mulai melakukan hal yang sama pada warga non-Papua.</p>\r\n\r\n<p style=\"text-align:justify\">&quot;Kurangi stigma perlebar komunikasi yang baik. Komnas HAM akan membantu membangun menciptakan Papua dengan berbagai dialog yang ada. Kami mengupayakan dengan sungguh-sungguh dialog dengan KKB,&quot; kata Anam.</p>\r\n\r\n<p style=\"text-align:justify\">Senada dengan Anam, anggota Komisi I Dewan Perwakilan Rakyat (DPR), Mayor Jenderal (Purnawirawan) TB Hasanuddin, mengatakan selama ini, pasukan non-organik cenderung bertindak tanpa pikir panjang dalam bertugas. Hal ini tidak terlepas dari mental bahwa mereka hanya bertugas sementara di Papua. Ini juga diperparah dengan kemampuan pasukan non-organik yang cenderung tidak menguasai medan dan situasi sosial di tempatnya bertugas.</p>\r\n\r\n<p style=\"text-align:justify\">&quot;Saya sepakat satuan non-organik dikurangi secara bertahap. Sehingga cukup satuan-satuan di sana saja. Dan ini (pendekatan teritorial) konsepnya kan merangkul, kalau konsep merangkul tak perlu pakai satuan tempur,&quot; kata TB Hasanuddin.</p>\r\n\r\n<p style=\"text-align:justify\">Meski begitu, aktivis HAM dari Papua, Yones Douw, meragukan wacana penarikan pasukan non-organik ini akan betul-betul diambil oleh TNI. Ia mengatakan hingga hari ini saja, operasi militer masih tetap berjalan di sejumlah daerah di Papua seperti Nduga, Intan jaya, Puncak Papua, Yahukimo, Pegunungan Bintang, hingga Maybrat.</p>\r\n\r\n<p style=\"text-align:justify\">Baku tembak antara aparat dengan kelompok pro-kemerdekaan Papua seperti Tentara Pembebasan Nasional Papua Barat - Organisasi Papua Merdeka (TPNPB-OPM) pun masih kerap terjadi. Apalagi Yones mengatakan saat ini sistem persenjataan yang digunakan kelompok pro-kemerdekaan tidak lagi menggunakan anak panah dan busur.</p>\r\n\r\n<p style=\"text-align:justify\">&quot;Sekarang TPN-OPM memiliki senjata modern sama seperti TNI-Polri. Jadi untuk tarik pasukan tidak mungkin,&quot; kata Yones.</p>\r\n\r\n<p style=\"text-align:justify\">Yones semakin meragukan rencana ini setelah Andika juga mengatakan akan mulai memekarkan pos-pos militer. Penambahan pos militer ini ia nilai dilakukan untuk membatasi ruang gerak TPNPB-OPM.</p>\r\n\r\n<p style=\"text-align:justify\">&quot;Jadi singkatnya itu penarik pasukan non-organik Mereka ditempatkan dan dikembalikan ke pos-pos baru di Papua,&quot; kata Yones.</p>\r\n', '2021-12-18', 4, 4),
(10, 'Ketua Umum Muhammadiyah Pesan ke Dudung Abdurachman Supaya Jaga Persatuan', 5, 'rido3.jpg', '<p style=\"text-align:justify\"><strong>Jakarta</strong>&nbsp;- Kepala Staf TNI Angkatan Darat (KSAD), Jenderal Dudung Abdurachman&nbsp;sowan kepada Ketua Umum Pimpinan Pusat Muhammadiyah, Haedar Nashir di Gedung PP Muhammadiyah Yogyakarta, Sabtu, 11 Desember 2021.</p>\r\n\r\n<p style=\"text-align:justify\">Bersama Dudung, Haedar membahas pentingnya persatuan nasional dengan cara merawat kebhinekaan sekaligus menjunjung tinggi prinsip musyawarah, kolektivitas dan gotong-royong royong. Haedar mengingatkan, persatuan menjadi hal yang mutlak bagi masa depan Indonesia.</p>\r\n\r\n<p style=\"text-align:justify\">&quot;Jangan sampai bangsa Indonesia pecah karena perbedaan-perbedaan yang tidak bisa kita dialog kan, tidak bisa kita cari titik temunya dan tentu karena perbedaan-perbedaan yang membuat kita makin menjauh satu sama lain,&quot; ujar Haedar dikutip dari keterangannya di laman resmi Muhammadiyah, Ahad, 12 Desember 2021.</p>\r\n\r\n<p style=\"text-align:justify\">Dalam kesempatan itu, Haedar mengungkapkan bahwa Muhammadiyah dan TNI&nbsp;memiliki kesamaan pandangan bahwa kehidupan kebangsaan harus berpijak pada tiga nilai, yaitu; pancasila, agama, dan kebudayaan luhur bangsa.</p>\r\n\r\n<p style=\"text-align:justify\">Seluruh agama di Indonesia, menurut Haedar, telah melewati berbagai proses panjang hingga menyatu dalam identitas ke-Indonesiaan.</p>\r\n\r\n<p style=\"text-align:justify\">Sementara itu, unsur kebudayaan luhur bangsa telah membentuk identitas nasional seperti sifat kebersamaan, gotong royong, dan keramahan bangsa Indonesia yang menjadi patokan bagi bangsa Indonesia dalam bersentuhan dengan kebudayaan asing.</p>\r\n\r\n<p style=\"text-align:justify\">&ldquo;Sehingga kita bisa belajar dari kebudayaan lain baik di Timur Tengah, di Asia, di Barat, tetapi semuanya juga harus tetap kita seleksi mana yang baik dan mana yang tidak pas dengan kebudayaan luhur bangsa. Nilai-nilai yang tidak sejalan dengan kebudayaan luhur bangsa, tentu jangan menjadi pola hidup bangsa Indonesia,&rdquo; kata Haedar.</p>\r\n\r\n<p style=\"text-align:justify\">Dengan nilai agama, Pancasila, dan nilai luhur kebudayaan bangsa ini, Haedar meyakini Indonesia akan semakin cerdas, dewasa, dan memiliki moralitas dan kepribadian yang luhur dalam menjalankan kehidupan berbangsa dan bernegara.</p>\r\n\r\n<p style=\"text-align:justify\">&quot;Alhamdulillah Pak KSAD juga memiliki pandangan yang sama tentang nilai-nilai luhur dalam kehidupan bangsa Indonesia tersebut,&quot; tutur Haedar Nashir.</p>\r\n\r\n<p style=\"text-align:justify\">Haedar menyebut Muhammadiyah dengan TNI selalu menjalin hubungan yang baik sebagaimana dengan Polri dan institusi pemerintah lain, karena punya sejarah yang panjang di mana Jenderal Sudirman sebagai kader dan tokoh Muhammadiyah menjadi panglima besar TNI pertama, sehingga nilai-nilai keprajuritan, perjuangan dan kepahlawanan melekat dalam Muhammadiyah.</p>\r\n\r\n<p style=\"text-align:justify\">&quot;Begitu juga dalam TNI ada jiwa, nilai-nilai agama dan perjuangan sebagaimana tokoh-tokoh Muhammadiyah melakukannya dan pergerakan Muhammadiyah selalu bersama bangsa dan negara,&rdquo; ujar Haedar.</p>\r\n\r\n<p style=\"text-align:justify\">Dalam pertemuan tersebut, Haedar ditemani oleh Sekretaris Pimpinan Pusat Muhammadiyah Agung Danarto. Sementara itu Jenderal Dudung Abdurachman&nbsp;ditemani oleh Panglima Komando Daerah Militer (PANGDAM) IV/Diponegoro, Mayor Jenderal TNI Rudianto bersama jajaran.</p>\r\n', '2021-12-18', 4, 3),
(11, 'Gerindra Buka Koalisi dengan PDIP, Bagaimana Peluang Duet Prabowo-Puan di Pilpres 2024?', 5, 'rido4.jpeg', '<p style=\"text-align:justify\">Sekretaris Jenderal Partai Gerindra&nbsp;Ahmad Muzani mengatakan bahwa hubungan partainya dengan PDIP saat ini sangat baik. Sehingga sangat memungkinkan berkoalisi di Pemilu 2024.</p>\r\n\r\n<p style=\"text-align:justify\">&quot;Hubungan kami dengan PDIP di Senayan, di kantor bagus. Saya dengan Pak Hasto baik, teman-teman bagus. Jadi sebagai sebuah kemungkinan koalisi ada,&quot; kata Muzani usai acara Kongres Tidar, Jakarta, Jumat (17/12/2021) kemarin.</p>\r\n\r\n<p style=\"text-align:justify\">Muzani mengatakan, sudah menghubungi PDIP&nbsp;untuk membicarakan capres dan cawapres. Namun, hingga saat ini PDIP masih menunggu keputusan Megawati Soekarnoputri.</p>\r\n\r\n<p style=\"text-align:justify\">&quot;Itu akan lihat waktu nanti ya, saya kira juga terus, saya sudah bertanya sama kawan-kawan PDIP&nbsp;juga masih menunggu waktu, semuanya menunggu waktu,&quot; pungkasnya.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<h2 style=\"text-align:justify\">Deklarasi Prabowo-Puan</h2>\r\n\r\n<p style=\"text-align:justify\">Untuk diketahui pada 29 November lalu, Dewan Presidium Nasional Poros Prabowo-Puan mendeklarasikan pasangan Prabowo Subianto dan Puan Maharani untuk menjadi calon presiden dan wakil presiden pada Pemilihan Umum 2024 mendatang.</p>\r\n\r\n<p style=\"text-align:justify\">Koordinator Presidium Nasional poros Prabowo-Puan, Andianto mengatakan, deklarasi pasangan Capres-Cawapres tersebut adalah yang pertama dilaksanakan di wilayah Banten.</p>\r\n\r\n<p style=\"text-align:justify\">&quot;Ini deklarasi untuk wilayah Provinsi Banten, untuk provinsi yang pertama. selanjutnya kami juga akan mendeklarasikan ke provinsi-provinsi lainnya,&quot; kata Adianto di resto Sae Pisan, BSD, Tangerang Selatan, Senin (29/11/2021).</p>\r\n', '2021-12-18', 4, 2),
(12, 'Berita Teknologi Terpopuler, dari PUBG: New State hingga AWS', 6, 'n.jpg', '<p style=\"text-align:justify\">Jakarta: Menit.com merangkum berita teknologi terpopuler atau berita yang paling banyak dibaca pada hari Kamis, 11 November 2021 yang mungkin terlewat oleh pembaca Menit.com<br />\r\n&nbsp;<br />\r\nDi bawah ini, Menit.com menyajikan rangkuman dari masing-masing berita teknologi terpopuler dilengkapi dengan tautan yang dapat diakses untuk menuju berita lengkap dari tiga berita teknologi terpopuler.<br />\r\n&nbsp;<br />\r\n<strong>1. PUBG: New State Rilis Hari Ini, Gaskeun!</strong></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\nPenggemar game battle royale PUBG di smartphone jangan sampai melupakan momentum kehadiran versi terbaru dari game tersebut hari ini. PUBG: New State dipastikan meluncur di lebih dari 200 negara dan bisa diunduh per hari ini, 11 November 2021.</p>\r\n\r\n<p style=\"text-align:justify\">Menit.com yang sudah bergabung dalam tahap pra-registrasi sejak lama kini sudah mendapatkan notifikasi dari Google Play Store untuk bisa downlod PUBG: New State. Di sini Anda mungkin akan disarankan untuk mengunduh dengan koneksi WiFi karena ukuran unduhan atau download size PUBG: New State mencapai 1,3GB.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>2. Pojok Usaha Wujudkan Dukungan AWS untuk Digitalisasi UMKM</strong></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\nAmazon Web Service mewujudkan dukungannya untuk membantu pelaku Usaha Mikro, Kecil dan Menengah (UMKM) dalam memanfaatkan ekosistem digital, termasuk warung sembako, via layanan hasil kolaborasinya dengan Midtrans, Pojok Usaha.<br />\r\n&nbsp;<br />\r\nGunawan menambahkan bahwa layanan Pojok Usaha menjadi salah satu wujud upaya AWS untuk menjangkau seluruh lini dan pemangku kepentingan atau stakeholder guna memajukan UMKM di Indonesia.<br />\r\n&nbsp;<br />\r\n<strong>3. Begini Ukuran Download dan Spesifikasi Game PUBG: New State</strong></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\nPUBG: New State hari ini resmi diliris hari ini, 11 November 2021, di Android dan iOS. Rencana kehadirannya sudah sesuai jadwal yang diberikan oleh pihak Krafton saat menggelar showcase pada bulan Oktober. PUBG: New State dirilis di lebih dari 200 negara.</p>\r\n\r\n<p style=\"text-align:justify\">Game ini hadir dengan 17 bahasa berbeda dan membawa konten baru dengan latar yang berbeda dari versi PUBG Mobile. Hal ini memang sudah diprediksi sejak awal karena PUBG: New State digarap oleh Krafton pemilik dan pencipta PUBG sementara PUBG Mobile dibuat Lightspeed &amp; Quantum Studio milik Tencent.</p>\r\n', '2021-12-18', 5, 0),
(13, 'Apakah Teknologi 5G Menggantikan 4G?', 6, 'a.jpg', '<p style=\"text-align:justify\">Menit.com, JAKARTA --&nbsp;Perusahaan teknologi Ericsson mengatakan teknologi generasi kelima (5G) akan membawa manfaat lebih banyak dibandingkan para pendahulunya. Dikutip dari keterangannya pada Kamis (2/12), Ericsson mengatakan 5G dirancang untuk memberikan lebih banyak kapasitas dalam jaringan.</p>\r\n\r\n<p style=\"text-align:justify\">Lebih banyak &quot;ruang&quot; untuk digunakan, yang berarti ada lebih banyak ruang bagi semua orang dan perangkat pun akan mendapatkan kecepatan data yang lebih tinggi. &quot;Sederhananya, teknologi 5G memungkinkan lebih banyak perangkat untuk terhubung sekaligus. Generasi jaringan seluler sebelumnya seperti 3G dan 4G mengalami kesulitan dalam memberikan performa konsisten apabila terhubung pada terlalu banyak device di tempat yang bersamaan,&quot; kata Ericsson.</p>\r\n\r\n<p style=\"text-align:justify\">Masih ada beberapa kesalahpahaman bahwa peluncuran 5G menjadi akhir dari era LTE. Sebenarnya memiliki 5G tidak berarti keberadaan 4G akan langsung menjadi usang.</p>\r\n\r\n<p style=\"text-align:justify\">Transisi ini berbeda dari pergeseran generasi seluler sebelumnya, yang berarti 4G bisa berguna lebih lama. Perpindahan dari 4G ke 5G berbeda dari peningkatan jaringan sebelumnya. 5G tidak menggantikan 4G, seperti bagaimana 4G menyalip 3G.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Akan ada transisi yang mulus dari 4G ke 5G, faktanya 4G akan ada bersama-sama dengan 5G hingga ekosistem, jaringan, dan aplikasi berevolusi ke generasi terbaru. 5G akan digunakan dalam operasi multi-band termasuk di band 4G yang sudah ada.</p>\r\n\r\n<p style=\"text-align:justify\">Perusahaan mengatakan 5G dapat mendukung pengalaman hiburan yang lebih total. Pengalaman baru ini didukung dengan&nbsp;<em>enhanced mobile broadband,</em>&nbsp;kecepatan jaringan yang sangat tinggi, latensi rendah, dan kapasitas data yang besar. Berkat teknologi IoT (Internet of Things), jaringan 5G menawarkan tingkat konektivitas yang berbeda.</p>\r\n\r\n<p style=\"text-align:justify\">Dengan jaringan 5G, pengguna dapat mengontrol dan terhubung dengan setiap perangkat yang mendukung konsep smart living, yang membuat hidup jauh lebih efisien dan produktif. Teknologi generasi kelima ini juga menawarkan kecepatan internet berkecepatan tinggi, hingga 10 kali lebih cepat dari jaringan 4G.</p>\r\n\r\n<p style=\"text-align:justify\">Selain itu, pengguna dapat melakukan streaming video dan film berkualitas tinggi seperti 4K atau bahkan 8K dengan lancar tanpa lag dan visual buram. Ericsson mengatakan, 5G akan mendukung extended reality (XR), yang mencakup teknologi Virtual Reality (VR), Augmented Reality (AR), dan Mixed Reality (MR).</p>\r\n\r\n<p style=\"text-align:justify\">Teknologi XR di era 5G diharapkan dapat meningkatkan produktivitas dan kenyamanan bagi konsumen, perusahaan, dan lembaga publik di berbagai sektor seperti hiburan, training, pendidikan, remote support, remote control, komunikasi, dan virtual meeting.</p>\r\n', '2021-12-18', 5, 6),
(14, 'Berita Teknologi Terpopuler, dari Sinarmas dan JD.ID hingga iMac M1 Pro M1 Max', 6, 'd.jpg', '<p style=\"text-align:justify\">Jakarta: Menit.com merangkum berita teknologi terpopuler atau berita yang paling banyak dibaca pada hari Selasa, 2 November 2021 yang mungkin terlewat oleh pembaca Menit.com.<br />\r\n&nbsp;<br />\r\nDi bawah ini, Medcom.id menyajikan rangkuman dari masing-masing berita teknologi terpopuler dilengkapi dengan tautan yang dapat diakses untuk menuju berita lengkap dari tiga berita teknologi terpopuler.<br />\r\n&nbsp;<br />\r\n<strong>1. Gandeng Sinar Mas Land, Beli Rumah dan Apartement Bisa Lewat JD.ID</strong></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\nlatform ecommcer JD.ID&nbsp; melakukan inovasi dengan menyediakan layanan transaksi atau jual beli rumah serta apartemen. Hal ini merupakan hasil kerja sama anatara JD.ID dengan bisnis properti dari Sinar Mas Land yang dimulai sejak bulan Agustus lalu untuk mendukung pertumbuhan ekonomi digital di Indonesia.<br />\r\n&nbsp;<br />\r\nPihak JD.ID sendiri mengakui bahwa mereka berkaca dari data Kementerian Komunikasi dan Informatika di tahun 2021 yang mengungkap bahwa pengguna smartphone di Indonesia mencapai 167 juta atau 89 persen dari total penduduk Tanah Air.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>2. iPhone Bakal Otomatis Telepon Darurat Saat Deteksi Kecelakaan</strong></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\nApple berencana mengembangkan fitur canggih yang bakal membuat kehidupan penggunanya tidak hanya mudah tapi juga aman. Mereka sedang menyiapkan fitur canggih di iPhone yang bisa mendeteksi apabila penggunanya mengalami kecelakaan.<br />\r\n&nbsp;<br />\r\nDi sebuah dokumen internal yang beredar, Apple disebut merencanakan kehadiran fitur cerdas berupa crash detection atau pendeteksi kecelakaan yang namanya belum diputuskan. Fitur ini disebut bakal hadir untuk iPhone dan Apple Watch tahun depan.<br />\r\n&nbsp;<br />\r\n<br />\r\n&nbsp;<br />\r\n<strong>3. Tahun Depan Bakal ada iMac dengan Chipset M1 Pro/Max</strong></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\nPada awal tahun 2021 ini, Apple secara resmi meluncurkan iMac dengan dukungan chipset prosesor M1 pertamanya. Perangkat ini hadir dalam model berlayar 24 inci dan mengusung desain berbeda dengan iMac generasi sebelumnya.<br />\r\n&nbsp;<br />\r\nMenurut tweet leakster @dylandkt, Apple diperkirakan akan merilis versi pembaruan dari iMac pada semester pertama tahun 2022. Namun, versi baru iMac ini akan hadir dengan dukungan chipset M1 Pro dan M1 Max.</p>\r\n', '2021-12-18', 5, 0),
(15, 'Guru Besar Ekonomi Unpad: Teknologi Jadi Nyawa Perkembangan Bisnis', 6, 'i.jpg', '<p style=\"text-align:justify\">Menit.com, JAKARTA - Guru Besar Fakultas Ekonomi Universitas Padjajaran Ilya Avianti menyampaikan perkembangan fintech di Indonesia dan dimulai dari sistem manajemen informasi.<br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Kemudian, bergerak begitu cepat, karena adanya perubahan yang sangat eksponensial dari teknologi. Setelah itu, kata dia, hadir digitalisasi sebagai penggerak utama dalam perekonomian, terbukti saat e-commerce yang bertumbuh cepat di masa pandemi. &ldquo;Bisnis akan tumbang tanpa adanya teknologi. Sehingga, Teknologi menjadi nyawa bagi perkembangan bisnis&rdquo; ujar penulis Buku Ekosistem Fintech Di Indonesia itu.<br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Hal itu disampaikan Ilya dalam acara bedah buku di Universitas Pembangunan Nasional (UPN) Veteran Jakarta Selasa (30/11) yang dipandu oleh CEO Nexus Risk Mitigation &amp; Strategic Communication.<br />\r\n<br />\r\nLebih lanjut, Ilya mengatakan fenomena fintech di Indonesia ditunjukkan dengan penggunaan PayLater yang sudah dapat digunakan oleh seluruh lapisan masyarakat, geliat ekonomi digital di era pandemi, dan bertumbuhnya Peer to Peer (P2P). Hadirnya fintech membawa beberapa harapan bagi perkembangan aspek keuangan di Indonesia, yakni mengubah governance menjadi lebih baik, mendorong inklusi keuangan &ldquo;Sehingga masyarakat dapat memiliki akses ke berbagai layanan keuangan formal, yang berkualitas, tepat waktu, lancar dan aman&rdquo; kata Ilya.<br />\r\n<br />\r\nKepala grup inovasi keuangan digital OJK Triyono Gani menjelaskan terdapat ekosistem dalam aspek keuangan. Menurut dia, sudah ada pemain-pemain awak dalam suatu ekosistem sehingga apabila ada pendatang baru, maka ia harus menyesuaikan, permasalahannya adalah ia diterima atau tidak.<br />\r\n<br />\r\n&ldquo;Walaupun fintech&nbsp;prinsip dasarnya sebagai penyedia jasa keuangan, namun fintech tetaplah pendatang baru. Sehingga fintech sebagai pendatang baru harus berperilaku baik, supaya tidak diperangi, dikucilkan&rdquo; tambah Triyono.<br />\r\n<br />\r\nTriyono menekankan bahwa sangat penting untuk menata pembagian governance. Triyono menyebut OJK saat ini berusaha melakukan pemisahan dan pemilahan supaya tidak terjadi disrupsi. Walaupun tidak dihindarkan untuk beberapa area dan akan menimbulkan irisan masing industri jasa keuangan memiliki porsi yang sama sesuai peranan. &ldquo;OJK sangat mendukung adanya kolaborasi dan menentang adanya head to head competition,&rdquo; ucap salah satu penulis Buku Ekosistem Fintech&nbsp;Di Indonesia.<br />\r\n<br />\r\nDekan Fakultas Ekonomi dan Bisnis&nbsp;UPN Veteran Jakarta Dianwicaksih Arieftiara mengatakan banyaknya penduduk Indonesia yang masih belum tersentuh jasa keuangan membuka peluang bagi industri jasa keuangan berbasis teknologi informasi atau fintech. Kendati demikian, terdapat pula tantangan bagi perkembangan fintech di Indonesia. &quot;Kurangnya literasi keuangan masyarakat yang akhirnya membuat mereka salah paham dengan hakikat dari fintech itu sendiri,&quot; ujar Dian.<br />\r\n<br />\r\n&nbsp;</p>\r\n', '2021-12-18', 5, 2),
(16, 'Berita Teknologi Terpopuler, dari Instagram Stories hingga Data Seluler', 6, 'f.jpg', '<p style=\"text-align:justify\">Jakarta: Menit.com merangkum berita teknologi terpopuler atau berita yang paling banyak dibaca pada hari Jumat, 17 Desember 2021 yang mungkin terlewat oleh pembaca Menit.com.<br />\r\n&nbsp;<br />\r\nDi bawah ini, Medcom.id menyajikan rangkuman dari masing-masing berita teknologi terpopuler dilengkapi dengan tautan yang dapat diakses untuk menuju berita lengkap dari tiga berita teknologi terpopuler.<br />\r\n&nbsp;<br />\r\n<strong>1. Instagram Uji Stories dengan Durasi Lebih Panjang</strong></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\nInstagram dilaporkan berupaya untuk memperpanjang durasi untuk salah satu fiturnya, Stories, menjadi 60 detik. Informasi ini disampaikan oleh Matt Navarra melalui tangkapan layar atau screenshot dari akun yousufortaccom yang dibagikan via Twitter miliknya.</p>\r\n\r\n<p style=\"text-align:justify\">Screenshot dari pengguna asal Turki ini mengungkap bahwa dirinya menemukan notifikasi yang menginformasikan mereka terkait dengan perubahan tersebut. Instagram telah menawarkan kreator cara untuk mengunggah konten melalui IGTV.<br />\r\n&nbsp;<br />\r\n<br />\r\n&nbsp;<br />\r\n<strong>2. Data Seluler Meningkat 300% dalam 10 Tahun</strong></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\nEricsson telah mengungkapkan bahwa data trafik seluler telah meningkat hampir 300 kali lipat pada tahun 2011. Tahun tersebut merupakan pertama kalinya laporan Ericsson Mobility Report diterbitkan.<br />\r\n&nbsp;<br />\r\nTemuan tersebut berdasarkan atas data jaringan terkini dan terdahulu, yang kemudian digabung ke dalam Ericsson Mobility Report edisi khusus 10 tahun. Laporan ini meninjau kembali beberapa tren utama dan peristiwa yang telah membentuk dekade terakhir, dan mengungkapkan prediksi hingga 2027.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>3. 4 dari 10 Pasien Khawatir Masalah Supply Chain di Industri Farmasi</strong></p>\r\n\r\n<p style=\"text-align:justify\"><br />\r\nZebra Technologies merilis temuan-temuan menarik dari Pharmaceutical Supply Chain Vision Study.<br />\r\n&nbsp;<br />\r\nPenelitian ini mendapati adanya ketidakpercayaan pasien terhadap obat yang mereka terima dan segmentasi di dalam supply chain farmasi, termasuk pihak yang memproduksi, mendistribusikan, meresepkan dan mengeluarkan obat-obatan tersebut.</p>\r\n', '2021-12-18', 5, 2),
(17, 'Cara Menambah Tinggi Badan yang Alami dan Sehat', 3, 'a3.jpg', '<p><strong>Bila ingin memiliki tinggi badan ideal, sebaiknya lakukan cara menambah tinggi badan yang alami dan sehat. Sebisa mungkin hindari penggunaan obat atau suplemen penambah tinggi instan yang banyak beredar di pasaran, karena ini belum tentu aman dan cocok digunakan.</strong></p>\r\n\r\n<p>Saat memasuki&nbsp;<a href=\"https://www.alodokter.com/pubertas-mengubah-tubuhku\" target=\"_blank\">masa pubertas</a>, seorang remaja akan mengalami masa percepatan pertumbuhan (<em>growth spurt</em>), termasuk pertumbuhan tinggi badan. Oleh sebab itu, sebaiknya cara menambah tinggi badan yang sehat dilakukan pada masa-masa ini.</p>\r\n\r\n<p>Pada remaja pria, percepatan pertumbuhan ini dimulai sejak usia 10&ndash;15 tahun. Sementara pada wanita remaja, masa ini dimulai sejak usia 8&ndash;13 tahun. Masa percepatan pertumbuhan dapat berlangsung sekitar 2&ndash;5 tahun, hingga seorang remaja mencapai kematangan fisik, termasuk tinggi badannya.</p>\r\n\r\n<p>Meski demikian, tidak semua orang bisa mencapai tinggi badan yang diinginkan. Ini karena pada dasarnya, tinggi badan seseorang dipengaruhi oleh berbagai faktor, termasuk faktor genetik atau keturunan.</p>\r\n\r\n<h3><strong>Cara Menambah Tinggi Badan&nbsp;</strong><strong>yang</strong>&nbsp;<strong>Alami</strong><strong>&nbsp;dan Sehat</strong></h3>\r\n\r\n<p>Ada beberapa cara yang mungkin dapat membantu menambah tinggi badan selama masa percepatan pertumbuhan, di antaranya:</p>\r\n\r\n<h4><strong>1. Olahraga secara rutin</strong></h4>\r\n\r\n<p>Olahraga dan aktivitas fisik rutin sangat baik untuk memperkuat jaringan otot dan tulang, mempertahankan berat badan yang sehat, dan meningkatkan produksi hormon pertumbuhan.</p>\r\n\r\n<p>Ada banyak jenis&nbsp;<a href=\"https://www.alodokter.com/fakta-tentang-olahraga-peninggi-badan\" target=\"_blank\">olahraga yang disarankan untuk menambah tinggi badan</a>&nbsp;secara alami, di antaranya bersepeda, berenang, sepak bola, badminton, dan basket. Namun, ingat, untuk mencegeah penularan COVID-19, olahraga tersebut sebaiknya olahraga dilakukan di rumah dan tidak beramai-ramai.</p>\r\n\r\n<h4><strong>2. Perbaiki postur tubuh</strong></h4>\r\n\r\n<p>Perbaikan postur tubuh juga bisa membantu menambah tinggi badan secara alami. Kebiasaan berdiri, duduk, dan tidur dengan postur yang tidak benar, misalnya sering membungkuk, dapat membuat tubuh terlihat terlihat lebih pendek. Sebaliknya, dengan postur tubuh yang baik dan tegak, tubuh akan terlihat lebih tinggi dan proporsional.</p>\r\n\r\n<p>Salah satu cara yang bisa dilakukan untuk memperbaiki postur tubuh dan membuat tubuh tampak lebih tinggi adalah dengan beberapa&nbsp;<a href=\"https://www.alodokter.com/8-cara-yoga-yang-wajib-diketahui-pemula\" target=\"_blank\">pose yoga</a>, seperti&nbsp;<em>mountain pose, cobra pose,&nbsp;</em>dan&nbsp;<em>child&rsquo;s pose.</em></p>\r\n\r\n<h4><strong>3. Istirahat yang cukup</strong></h4>\r\n\r\n<p>Kurang tidur dapat memicu berbagai masalah kesehatan. Pada anak-anak dan remaja, kurang tidur bisa membuat tumbuh kembang terganggu. Ini karena proses pertumbuhan dan perkembangan tubuh membutuhkan asupan energi yang tinggi, dan salah satu cara untuk mengisi energi tersebut adalah dengan istirahat yang cukup.</p>\r\n\r\n<p>Berikut adalah jumlah waktu tidur yang ideal berdasarkan usia:</p>\r\n\r\n<ul>\r\n	<li>Bayi 0&ndash;3 bulan: 14&ndash;17 jam</li>\r\n	<li>Bayi 3&ndash;11 bulan: 12&ndash;17 jam</li>\r\n	<li>Balita 1&ndash;2 tahun: 11&ndash;14 jam</li>\r\n	<li>Balita 3&ndash;5 tahun: 10&ndash;13 jam</li>\r\n	<li>Anak 6&ndash;13 tahun: 9&ndash;11 jam</li>\r\n	<li>Remaja 14&ndash;17 tahun: 8&ndash;10 jam</li>\r\n	<li>Orang dewasa 18&ndash;64 tahun: 7&ndash;9 jam</li>\r\n</ul>\r\n\r\n<h4><strong>4. Konsumsi makanan bergizi seimbang</strong></h4>\r\n\r\n<p>Selama masa pertumbuhan, sangat penting untuk memenuhi nutrisi yang dibutuhkan oleh tubuh, agar pertumbuhan tinggi badan bisa maksimal. Pada anak-anak, asupan nutrisi yang tercukupi juga berperan penting untuk mencegah&nbsp;<em><a href=\"https://www.alodokter.com/memahami-stunting-pada-anak\" target=\"_blank\">stunting</a>.</em></p>\r\n\r\n<p>Untuk menunjang pertumbuhan tinggi badan dan menjaga kepadatan tulang, anak-anak dan remaja perlu mengonsumsi&nbsp;<a href=\"https://www.alodokter.com/asupan-peninggi-badan-yang-bisa-membantu-pertumbuhan-anak\" target=\"_blank\">makanan</a>&nbsp;yang kaya akan beragam nutrisi, seperti protein, kalsium, dan vitamin D.</p>\r\n\r\n<p>Asupan nutrisi tersebut bisa diperoleh dari buah dan sayuran, kacang-kacangan, biji-bijian, ikan,&nbsp;<em>seafood</em>, telur, tahu tempe, serta susu dan olahannya, termasuk&nbsp;<em>yoghurt</em>&nbsp;dan keju.</p>\r\n\r\n<p>Pada beberapa kasus, misalnya gangguan pada&nbsp;<a href=\"https://www.alodokter.com/peran-penting-hormon-pertumbuhan\" target=\"_blank\">hormon pertumbuhan</a>&nbsp;atau malnutrisi, penggunaan suplemen tambahan mungkin akan disarankan untuk menambah tinggi badan. Namun, konsumsi suplemen ini sebaiknya disesuaikan dengan anjuran dokter.</p>\r\n\r\n<p>Selain itu, untuk mengatasi masalah pada hormon pertumbuhan, dokter juga bisa memberikan terapi hormon.</p>\r\n\r\n<p>Perlu diingat, beberapa cara menambah tinggi badan di atas umumnya akan lebih efektif untuk dilakukan pada anak-anak dan remaja. Sementara itu, untuk menambah tinggi badan pada orang dewasa, cara tersebut mungkin tidak akan efektif. Guna menambah tinggi badan pada orang dewasa, cara yang terbukti efektif adalah dengan operasi.</p>\r\n\r\n<p>Jika Anda merasa tidak percaya diri dengan tinggi badan Anda dan ingin memperbaikinya, atau Anda khawatir pertumbuhan badan anak Anda mengalami gangguan, sebaiknya konsultasikan ke&nbsp;<a href=\"https://www.alodokter.com/cari-dokter/dokter-ortopedi\" target=\"_blank\">dokter</a>&nbsp;agar bisa dilakukan pemeriksaan dan diberikan solusi yang tepat.</p>\r\n', '2021-12-21', 6, 1),
(18, 'Ingat! Ini 7 Vaksin Covid yang Sudah Diakui WHO, Ada Sinovac?', 3, 'a0.jpeg', '<p><strong>Jakarta, CNBC Indonesia -</strong>&nbsp;Seluruh dunia terus menggenjot vaksinasi Covid-19 untuk mencapai kekebalan kelompok (herd immunity).</p>\r\n\r\n<p>Organisasi Kesehatan Dunia (WHO) turut membantu dunia memerangi Covid-19 melalui COVAX Facility, sebuah program untuk pengiriman dan mendistribusikan vaksin Covid-19 bagi negara berkembang dan miskin.</p>\r\n\r\n<p>Vaksin yang masuk dalam program ini harus mendapatkan emergency use listing (EUL) dari WHO. EUL menilai kualitas, keamanan dan kemanjuran vaksin Covid-19 serta rencana manajemen risiko dan kesesuaian program seperti persyaratan cold chain (rantai dingin), seperti dikutip dari situs WHO, Rabu (15/12/2021).</p>\r\n\r\n<p>Berikut ini daftar vaksin Covid-19 yang mendapat izin EUL dari WHO:</p>\r\n\r\n<p><strong>1. Vaksin Sinopharm</strong></p>\r\n\r\n<p>WHO memerikan EUL pada vaksin Sinopharm yang diproduksi Beijing Bio-Institute of Biological Products Co Ltd, subsidiary of China National Biotec Group (CNBG) pada 7 Mei 2021. Vaksin ini menggunakan platform inactivated virus SARS-CoV-2.</p>\r\n\r\n<p>WHO merekomendasikan vaksin Sinopharm bagi orang dewasa berusia 18 tahun ke atas sebanyak dua dosis dengan jarak penyuntikan tiga hingga empat minggu. Vaksin Sinopharm memiliki efikasi 79%.</p>\r\n\r\n<p><strong>2. Vaksin Pfizer/BioNTech</strong></p>\r\n\r\n<p>WHO memberikan izin EUL pada vaksin Pfizer/BioNtech pada 31 Desember 2020. Vaksin ini menggunakan Comirnaty Covid-19 mRNA. Vaksin ini memiliki efikasi 95% lawan Covid-19.</p>\r\n\r\n<p><strong>3. Vaksin Moderna</strong></p>\r\n\r\n<p>WHO memberikan izin EUL pada vaksin Covid-19 produksi Moderna Biotech pada 30 April 2021. Vaksin ini menggunakan platform nucleoside yang dimodifikasi (mRNA). Efikasi vaksin ini mencapai 94% lawan Covid-19.&nbsp;</p>\r\n\r\n<p><strong>4. Vaksin AstraZeneca</strong></p>\r\n\r\n<p>WHO memberikan EUL pada vaksin AstraZeneca yang diproduksi oleh AstraZeneca dan SKBio (Korea Selatan) dan Serum Institute of India (India). Pemberian izin ini dilakukan pada 15 Februari 2021. Vaksin ini menggunakan platform virus vektor. Kedua memiliki efikasi 63,09%.</p>\r\n\r\n<p><strong>5. Vaksin Johnson &amp; Johnson</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>WHO memberikan EUL pada vaksin Covid-19 Johnson &amp; Johnson yang diproduksi Jassen pada 12 Maret 2021. Vaksin ini menggunakan platform virus vektor. Ini adalah vaksin dengan dosis tunggal.</p>\r\n\r\n<p><strong>6. Vaksin Sinovac</strong></p>\r\n\r\n<p>WHO memberikan EUL pada vaksin Covid-19 CoronaVac dari Sinovac BioNTech pada Maret 2021. Vaksin ini menggunakan platform virus yang dinonaktifkan. Ini adalah vaksin dua dosis dengan efikasi mencapai 65,3%.</p>\r\n\r\n<p><strong>7. Vaksin Covaxin</strong></p>\r\n\r\n<p>WHO menerbitkan EUL pada vaksin Covaxin buatan Bharat Biotech International pada 3 November 2021. Vaksin ini menggunakan platform virus yang dinonaktifkan. Efikasi vaksin mencapai 78%.</p>\r\n', '2021-12-21', 6, 1),
(19, 'Akibat Sekolah Daring bagi Kesehatan Mental Remaja', 3, 'a2.jpg', '<p>Tidak terasa pembelajaran daring sudah berlangsung selama 1 tahun lebih. Lalu, apakah teman-teman ada yang sudah bosan belajar dari rumah? Atau bahkan ada yang sudah nyaman dan tidak ingin melakukan proses pembelajaran tatap muka? Bosan atau nyaman dampak yang dirasakan remaja, pastinya sedikit-banyak berawal dari kondisi keluarga dan lingkungan.</p>\r\n\r\n<p>Mengutip klikdokter, Iksan Bella Persada, M.Psi., Psikolog., kesehatan mental remaja di masa pandemi bisa terganggu, karena mereka belum memiliki kemampuan problem solving, kontrol diri dan emosi yang tidak optimal.</p>\r\n\r\n<p>Alasan emosi tidak seimbang pada pelajar yang masih remaja ini selain karena perubahan hormon, mereka juga belum bisa beradaptasi dengan metode belajar baru yang sangat tiba-tiba. Remaja dipaksa melihat gawai dan laptop 8 jam dan selebihnya masih digunakan untuk mengerjakan tugas-tugas dari sekolah. Belum lagi masalah pelajar yang tidak bisa bertemu teman mereka, sehingga waktu bermain dan refreshing pun tidak ada.</p>\r\n\r\n<p>Akhirnya kesehatan mental remaja terganggu sehingga kemampuan siswa memahami materi berkurang dan semangat menyelesaikan tugas yang diberikan guru menurun. Bahkan sekarang joki tugas tersebar dimana-dimana dengan upah mulai Rp 1000,00 per lembar. Seakan ini menjadi kebiasaan baru bagi pelajar yakni semua bisa diselesaikan dengan uang, bukan dengan kemampuan diri sendiri. Hal yang ditakutkan di masa depan adalah semakin banyak pelajar yang tidak memiliki kerpercayaan diri atas kemampuannya karena sudah terdidik cukup lama dengan keadaan tersebut.</p>\r\n\r\n<p>Suasana tempat belajar juga mempengaruhi kesehatan mental remaja, seperti keluarga yang tak peduli pada proses pembelajaran. Kadang remaja diminta menjaga adiknya saat ibu mereka memasak atau bahkan menjaga toko. Disini remaja tidak lagi memiliki batas antara waktu belajar dan waktu membantu orang tua. Alhasil remaja sulit membedakan kegiatan mana yang harus diutamakan dan mudah kehilangan fokus karena semua tuntutan dari sekolah dan rumah tercampur.</p>\r\n\r\n<p>Selain dampak negatif yang dapat ditimbulkan pembelajaran daring, ada juga akibat positifnya sebagai berikut.</p>\r\n\r\n<p>1. Tidak boros</p>\r\n\r\n<p>Pelajar melakukan semua kegiatan belajar dari rumah sehingga tidak membutuhkan uang jajan seperti biasanya saat sekolah tatap muka. Bagi siswa perantauan pun mereka harus pulang kampung sehingga jatah uang kost atau kontrakan dapat ditabung.</p>\r\n\r\n<p>2. Dapat meringankan pekerjaan rumah</p>\r\n\r\n<p>Banyak keluarga memberhentikan ART karena kekurangan ekonomi sehingga kondisi rumah sering berantakan. Jadi, remaja sebagai anak dapat membantu menyelesaikan pekerjan rumah yang ringan saja, seperti mencuci piring.</p>\r\n\r\n<p>Lalu, bagaimana dengan remaja yang sudah terganggu kesehatan mentalnya? Yuk, simak beberapa cara mengatasinya!</p>\r\n\r\n<p>1. Orang tua mengajak anaknya melakukan deeptalk rutin tiap malam tentang kesehariannya untuk mengetahui ada masalah ataukah tidak. Sehingga keluarga sebagai circle pertama yang mendukung kesehatan mental remaja dapat maksimal membantu karena penyebabnya pada setiap remaja berbeda-beda</p>\r\n\r\n<p>2. Memberikan motivasi dan energi positif oleh orang tua pada anaknya. Karena manusia memilki cara berpikir dan melakukan sesuatu sesuai dengan asupan dan lingkungan. Jadi apabila semua yang masuk ke tubuhnya hal baik maka yang dihasilkan pun baik</p>\r\n\r\n<p>3. Jika sudah tidak dapat ditangani maka perlu dibawa ke psikiater. Semoga saja hal in tidak terjadi, akan tetapi jika memang diharuskan maka pilihlah psikiater yang tepat. Apabila terkendala ekonomi maka bisa menggunakan BPJS. Jadi jangan segan untuk cek kesehatan mental dan mengobatinya!</p>\r\n', '2021-12-21', 6, 0),
(20, 'Kasus Pertama Omicron di Indonesia Diduga dari WNI yang Datang dari Nigeria', 3, 'a4.jpeg', '<p>Jakarta, 19 Desember 2021</p>\r\n\r\n<p>Kementerian Kesehatan telah melakukan pelacakan asal muasal masuknya virus Covid-19 varian Omicron ke Indonesia dengan kasus pertama diduga berasal dari warga negara Indonesia (WNI) yang tiba dari Nigeria pada tanggal 27 November 2021.</p>\r\n\r\n<p>Sebelumnya pada Kamis (16/12) Menteri Kesehatan RI Budi Gunadi Sadikin mengumumkan temuan kasus varian Omicron terdeteksi pada seorang petugas kebersihan berinisial N yang bekerja di RSDC Wisma Atlet Kemayoran Jakarta.</p>\r\n\r\n<p>N tidak pernah melakukan perjalanan ke luar negeri sehingga dapat disimpulkan N tertular dari WNI yang datang dari luar negeri yang melakukan karantina di Wisma Atlet.</p>\r\n\r\n<p>Setelah merunut kasus WNI yang positif Covid-19 di Wisma Atlet pada 14 hari ke belakang, kemungkinan besar indeks case (kasus pertama) Omicron adalah WNI, dengan inisial TF, usia 21 tahun, yang tiba dari Nigeria pada tanggal 27 November 2021.</p>\r\n\r\n<p>Ada 169 WNI dari luar negeri yang melakukan karantina di Wisma Atlet antara 24 November hingga 3 Desember 2021 yang telah dilakukan tracing dengan hasil satu orang, TF, probable dengan kemungkinan besar tertular Omicron.</p>\r\n\r\n<p>Hasil test PCR untuk TF sudah dinyatakan negative.</p>\r\n\r\n<p>Juru Bicara Vaksinasi COVID-19 Kementerian Kesehatan RI dr. Siti Nadia Tarmidzi, M.Epid mengatakan terdeteksinya kasus pertama Omicron di Indonesia merupakan salah satu fungsi utama dari karantina bagi setiap orang yang masuk ke negara Indonesia.</p>\r\n\r\n<p>Melalui karantina, pelaku perjalanan dari luar negeri akan dipantau dan diobservasi oleh petugas kesehatan. Dengan demikian apabila pelaku perjalanan tersebut didapati positif COVID-19 bisa dengan segera dilakukan tracing.</p>\r\n\r\n<p>Tidak hanya itu, melalui karantina pula pelaku perjalanan yang terkonfirmasi positif COVID-19 dengan gejala bisa langsung ditangani petugas medis.</p>\r\n\r\n<p>&ldquo;Penting bagi setiap pelaku perjalanan luar negeri yang masuk ke Indonesia untuk melakukan karantina. Terdeteksinya Omicron di Indonesia merupakan salah satu keberhasilan dari karantina dan kita bisa dengan segera melakukan tracing untuk mencegah meluasnya penularan Omicron,&rdquo; katanya dr. Nadia.</p>\r\n\r\n<p>Ia mengimbau masyarakat untuk tetap mewaspadai penyebaran Omicron dan virus COVID-19 jenis lainnya.</p>\r\n\r\n<p>&ldquo;Kurangi mobilitas, tetap gunakan masker, rajin mencuci tangan dengan sabun, dan menjaga jarak. Jangan lengah dan tetap waspada terhadap penularan virus COVID-19, terutama omicron yang laju penyebarannya sangat cepat.</p>\r\n\r\n<p>Varian Omicron yang memiliki daya tular lima kali lipat dari varian Delta, merebak luas pertama kali di negara-negara Afrika bagian selatan.</p>\r\n', '2021-12-21', 6, 1),
(21, 'Jangan Keliru, Ini Syarat Perjalanan di Masa Liburan Nataru', 3, 'a1.png', '<p><strong>Jakarta, CNBC Indonesia</strong>&nbsp;- Kementerian Perhubungan (Kemenhub) menegaskan tidak akan ada penyekatan di semua sarana dan prasarana transportasi, melainkan hanya pengetatan protokol kesehatan di masa libur Nataru. Hal ini disampaikan langsung oleh Juru Bicara Kementerian Perhubungan Adita Irawati.<br />\r\n<br />\r\nIa menuturkan, merujuk pada Surat Edaran (SE) Satgas Nomor 24 dan Instruksi Menteri Dalam Negeri Nomor 66 dan 67, Kemenhub menetapkan pengendalian transportasi dan petunjuk pelaksanaan penumpang dalam negeri melalui SE Nomor 109 untuk transportasi darat, SE Nomor 110 untuk transportasi laut, SE Nomor 111 untuk transportasi udara, SE Nomor 112 untuk Kereta. Ketentuan itu berlaku mulai 24 Desember 2021 hingga 2 Januari 2022.<br />\r\n<br />\r\nAdapun isi umum mengenai surat edaran ini adalah, pertama, yang bisa melakukan perjalanan di dalam negeri adalah penumpang yang sudah memenuhi vaksin lengkap atau 2 kali dosis dan disertai dengan hasil negatif tes antigen yang berlaku 1 x 24 jam yang juga harus menggunakan aplikasi PeduliLindungi.<br />\r\n<br />\r\n&quot;Bagi yang belum mendapatkan vaksin lengkap, untuk sementara waktu mobilitasnya dibatasi. Adapun bagi anak usia 12 tahun ke bawah wajib menunjukkan hasil negatif tes antigen 3 x 24 jam,&quot; ujar Adita dalam keterangan pers virtual, Senin (20/12/2021).<br />\r\n<br />\r\nKedua, adalah ketentuan mengenai pembatasan kapasitas di semua moda transportasi. Untuk transportasi darat, nantinya akan diatur sesuai dengan level PPKM dan jenis transportasinya. Untuk kapasitas transportasi laut adalah sebesar 75% dari kapasitas maksimal. Untuk transportasi udara 100% dari kapasitas maksimal dengan syarat harus menyediakan 3 baris kosong agar dapat menyediakan bagi penumpang yang menunjukkan gejala sakit.<br />\r\n<br />\r\n&quot;Sedangkan untuk moda transportasi kereta api dibatasi sebesar 80% untuk kereta api antar kota lalu kereta api lokal perkotaan sebesar 70% sedangkan kereta api perjalanan rutin atau KM dalam aglomerasi dibatasi sebesar 45%,&quot; papar Adita.<br />\r\n<br />\r\nAdita menambahkan, dalam hal mengantisipasi masuknya varian omicron ke Indonesia, pintu masuk internasional saat ini sudah dibatasi. Kemenhub, lanjut dia, selalu bekerja sama dengan berbagai pihak terkait seperti Keimigrasian, Bea Cukai, Kementerian Kesehatan, dan Satgas Penanganan Covid-19.</p>\r\n', '2021-12-21', 6, 0),
(22, 'Italia Gagal Lolos Langsung ke Piala Dunia 2022', 4, 'irlandia-utara-vs-italia-3_169.jpeg', '<p>&nbsp;</p>\r\n\r\n<p>Jakarta, Indonesia --&nbsp;<strong><a href=\"https://www.cnnindonesia.com/tag/timnas-italia\" target=\"_blank\">Timnas Italia</a></strong>&nbsp;gagal lolos langsung ke&nbsp;<strong><a href=\"https://www.cnnindonesia.com/tag/piala-dunia-2022\" target=\"_blank\">Piala Dunia 2022</a></strong>&nbsp;usai ditahan imbang 0-0 oleh Irlandia Utara di Windsor Park, Belfast, Selasa (16/11) dini hari WIB.</p>\r\n\r\n<p>Kegagalan meraih kemenangan di markas Irlandia Utara membuat Italia menempati posisi runner up Grup C Kualifikasi Piala Dunia 2022. Italia digeser Swiss yang keluar sebagai juara grup usai menang telak 4-0 atas Bulgaria pada saat yang bersamaan.</p>\r\n\r\n<p>Swiss naik ke puncak klasemen dengan 18 poin. Sedangkan Italia turun ke posisi kedua karena hanya mengoleksi 16 poin dan harus menerima kenyataan tampil di babak playoff untuk bisa lolos ke Piala Dunia 2022.</p>\r\n\r\n<p>Italia yang butuh kemenangan mengambil inisiatif serangan di laga ini. Di sisi lain, Irlandia Utara bermain defensif sembari melihat peluang melakukan serangan balik ke pertahanan tim tamu.</p>\r\n\r\n<p>Italia menciptakan peluang pada menit ke-32. Chiesa melakukan penetrasi ke kotak penalti Irlandia Utara yang diakhiri tendangan kaki kiri tetapi masih terlalu lemah dan mudah diamankan kiper lawan.</p>\r\n\r\n<p>Lima menit berselang Italia kembali mendapatkan peluang untuk mencetak gol. Insigne berhasil masuk ke kotak penalti tetapi tendangannya masih mudah diamankan kiper Irlandia Utara, Bailey Peacock-Farrell.</p>\r\n\r\n<p>Irlandia Utara nyaris unggul lebih dulu 1-0 di awal babak kedua. Umpan mendatar Jamal Lewis disambut tendangan kaki kiri George Saville tetapi bola yang mengarah ke gawang bisa dimentahkan oleh Gianluigi Donnarumma.</p>\r\n\r\n<p>Italia mendapatkan peluang emas untuk mencetak gol pada menit ke-63. Chiesa bisa mengecoh satu pemain Irlandia Utara usai menerima umpan Domenico Berardi tetapi tembakan kaki kiri pemain Juventus itu ke tiang jauh berada tipis di sisi kanan gawang.</p>\r\n\r\n<p>Italia terus gencar melakukan tekanan tetapi kesulitan menembus pertahanan Irlandia Utara yang tampil solid. Gli Azzurri bahkan nyaris kebobolan jelang akhir pertandingan.</p>\r\n\r\n<p>Kesalahan Donnarumma dalam membaca arah bola justru membahayakan gawang Italia. Namun, tembakan Conor Washington masih terlalu lemah sehingga bisa dibaca pemain belakang Italia.</p>\r\n\r\n<p>Italia tidak kunjung mampu mencetak gol di sisa waktu pertandingan. Alhasil, skor imbang 0-0 melawan Irlandia Utara bertahan hingga babak kedua usai.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Susunan Pemain</h2>\r\n\r\n<p>Irlandia Utara (5-3-2): Bailey Peacock-Farrell; Stuart Dallas, Tom Flanagan, Jonny Evans, Craig Cathcart, Jamal Lewis; Alistair Mccan, Steven Davis, George Saville; Gavin Whyte, Josh Magennis</p>\r\n\r\n<p>Italia (4-3-3): Gianluigi Donnarumma; Giovanni Di Lorenzo, Leonardo Bonucci, Francesco Acerbi, Emerson; Sandro Tonali, Jorginho, Nicolo Barella; Domenico Berardi, Lorenzo Insigne, Federico Chiesa</p>\r\n', '2021-12-22', 1, 0),
(23, 'Headband Kevin Sanjaya Bikin Geger Dunia Maya', 4, 'kevin-sanjaya-sukamuljo-1_169.jpeg', '<p>&nbsp;</p>\r\n\r\n<p>Jakarta, Indonesia -- Penampilan&nbsp;<strong><a href=\"https://www.cnnindonesia.com/tag/kevin-sanjaya\" target=\"_blank\">Kevin Sanjaya</a></strong>&nbsp;dengan headband di&nbsp;<strong><a href=\"https://www.cnnindonesia.com/tag/bwf-world-tour-finals\" target=\"_blank\">BWF&nbsp;World Tour Finals 2021</a></strong>&nbsp;membuat heboh para penggemar badminton di dunia maya.</p>\r\n\r\n<p>Kevin menggunakan headband alias ikat kepala saat bersama Marcus Fernaldi Gideon menghadapi Lee Yang/Wang Chi Lin di laga pertama BWF World Tour Finals 2021.</p>\r\n\r\n<p>Penampilan Kevin yang berbeda dari biasanya itu mengundang banyak pujian dari penggemar badminton. Kevin dianggap netizen lebih mempesona dengan atribut tersebut.</p>\r\n\r\n<p>Video Kevin menggunakan headband sendiri sebelumnya sudah viral di dunia maya saat ia menggunakan headband saat menjalani latihan sehari sebelum BWF World Tour Finals digelar.</p>\r\n\r\n<p>Saat ditanya perihal aksesoris headband tersebut, Kevin menyatakan alasan di balik penggunaan headband tersebut lantaran rambutnya sudah makin panjang.</p>\r\n\r\n<p>&quot;Pakai headband ini karena belum sempat memotong rambut saja,&quot; ucap Kevin dalam rilis resmi PBSI.</p>\r\n\r\n<p>Di dunia badminton, sejumlah pemain kerap menggunakan headband saat bertanding. Pemain yang termasuk sering dijumpai menggunakan headband adalah Viktor Axelsen dari Denmark.</p>\r\n\r\n<p>Marcus Gideon sendiri juga sempat menggunakan headband ketika bertanding di Olimpiade lalu.</p>\r\n\r\n<p>Kevin/Marcus berhasil mengawali perjalanan mereka di BWF World Tour dengan kemenangan atas Lee/Wang, 23-21, 21-19.</p>\r\n\r\n<p>Di laga kedua hari ini, Kevin/Marcus bakal berhadapan dengan Satwiksairaj Rankireddy/Chirag Shetty dari India.</p>\r\n', '2021-12-22', 1, 0);
INSERT INTO `berita` (`id_berita`, `judul`, `id_kategori`, `gambar`, `isi_berita`, `tgl_posting`, `id_user`, `dilihat`) VALUES
(24, 'Kabar Terbaru Tentang Kasal Cup Olahraga Perairan 2021, Selamat untuk TNI AL', 4, 'ksal-laksamana-tni-yudo-margono-berfoto-bersama-usai-menyera-kzui.jpg', '<p><a href=\"https://www.jpnn.com/tag/jakarta\">JAKARTA</a>&nbsp;- TNI AL merajai perolehan medali pada Event Kasal Cup Olahraga Perairan 2021 yang berlangsung sejak tanggal 24 November dan ditutup secara resmi pada Minggu (28/11) oleh KSAL Laksamana TNI Yudo Margono, &nbsp;di Pantai Festival Ancol, Jakarta Utara.&nbsp;&nbsp;</p>\r\n\r\n<p>Hampir semua medali Cabang Olahraga (Cabor) yang dipertandingkan di&nbsp;<a href=\"https://www.jpnn.com/tag/kasal-cup\" title=\"Semua artikel tentang Kasal Cup\">Kasal Cup</a>&nbsp;Kompetisi Olahraga Air Tahun 2021 pada kategori TNI-Polri berhasil direbut oleh atlet-atlet dari TNI AL. Sementara untuk kategori Umum perolehan medali cukup merata.</p>\r\n\r\n<p>Dari data yang diperoleh dari panitia penyelenggara, atlet-atlet TNI AL berhasil menyapu bersih medali pada kategori TNI-Polri Cabor Ski Air, Cabor Renang, Dayung Perahu Naga Putra 12 Crew 200 M, dan Cabor Selam TNI-Polri Putra.</p>\r\n\r\n<p>Sementara itu pada Cabor Layar, 20 medali dari 33 medali yang diperebutkan berhasil diraih atlet-atlet TNI AL.&nbsp;&nbsp;</p>\r\n\r\n<p><a href=\"https://www.jpnn.com/tag/laksamana-yudo-margono\" title=\"Semua artikel tentang Laksamana Yudo Margono\">Laksamana Yudo Margono</a>&nbsp;dalam sambutannya menyampaikan seluruh kegiatan yang dilombakan berjalan dengan aman, tertib, dan lancar serta dapat diselesaikan sesuai jadwal waktu yang direncanakan.</p>\r\n\r\n<p>Hal tersebut bisa tercapai karena seluruh peserta, baik atlet, official maupun panitia telah bekerja dan berjuang dengan segenap kemampuan dan dengan penuh disiplin</p>\r\n\r\n<p>&ldquo;Juga kesungguhan yang tinggi termasuk di dalam penerapan protokol kesehatan sehingga seluruh rangkaian lomba berjalan sukses,&rdquo; ujar Laksamana Yudo.</p>\r\n\r\n<p>Di samping itu juga kesuksesan acara ini terlihat dari antusiasme peserta dan warga masyarakat yang mengindikasikan Olahraga Perairan makin diminati khususnya generasi muda bangsa.&nbsp;</p>\r\n\r\n<p>&ldquo;Hal ini menjadi indikator penunjang bahwa secara bertahap namun pasti telah tertanam kembali di dalam jiwa generasi penerus bangsa budaya maritim dan cinta bahari,&rdquo; ungkap Kasal.</p>\r\n\r\n<p>Lebih lanjut, Laksamana Yudo menyampaikan keberhasilan yang telah dicapai tersebut, terwujud atas kesungguhan hati dan kerja keras serta semangat pantang menyerah yang ditampilkan oleh seluruh peserta. &ldquo;Hal ini merupakan bukti nyata bahwa pembinaan Olahraga Perairan telah ditangani secara profesional dan dalam arti yang lebih luas telah memberikan kontribusi positif terhadap pembinaan Olahraga Perairan secara nasional,&rdquo; ujar Yudo. Selanjutnya, Laksamana Yudo mengucapkan selamat dan sukses kepada para pemenang, disertai harapan semoga kemenangan yang diraih makin meningkatkan prestasi dan kecintaan saudara terhadap olahraga perairan. Sementara kepada peserta yang pada kesempatan ini belum berhasil meraih kemenangan, Laksamana berpesan agar tidak kecewa, putus asa, dan berkecil hati.</p>\r\n\r\n<p>Dia berharap tetap bersemangat untuk meningkatkan kemampuan dan keterampilan dengan terus berlatih agar pada pertandingan berikutnya dapat memperoleh hasil yang lebih baik. Kasal Cup Olahraga Perairan 2021 diikuti 2.233 atlet dari seluruh Indonesia baik TNI/Polri maupun masyarakat umum untuk memperebutkan lebih kurang 400 piala. Adapun Cabor yang diperlombakan meliputi Selam (264 atlet/19 kontingen), Renang (327 atlet/30 kontingen), Ski Air (271 atlet), Jetski (80 atlet/5 kontingen), Layar (114 atlet/17 kontingen), dan Dayung (960 atlet/77 Tim).</p>\r\n', '2021-12-22', 1, 0),
(25, 'Target Desain Besar Olahraga Nasional: Capai 5 Besar Olimpiade dan Paralimpiade pada 2045', 4, 'paralimpiade.jpg', '<p><strong>JAKARTA&nbsp;</strong>&ndash; Target Desain Besar Olahraga Nasional (DBON) telah diumumkan. Indonesia ditargetkan bisa mencapai 5 besar&nbsp;<a href=\"https://www.okezone.com/tag/olimpiade\">Olimpiade&nbsp;</a>dan&nbsp;<a href=\"https://www.okezone.com/tag/paralimpiade\">Paralimpiade&nbsp;</a>pada 2045. Dengan kata lain itu merupakan hadiah untuk 100 tahun kemerdekaan Indonesia.</p>\r\n\r\n<p>Penyampaikan target Desain Besar Olahraga Nasional (DBON) ini disampaikan dalam sebuah acara yang digelar pada Rabu (10/11/2021). Agenda sosialisasi ini dipimpin oleh Kementerian Pemuda dan Olahraga (Kemenpora) RI.</p>\r\n\r\n<p><img alt=\"Olimpiade Tokyo 2020\" src=\"https://img-z.okeinfo.net/okz/500/library/images/2021/08/02/3381izr1ylu8dwbrlvd0_15644.jpg\" style=\"height:332px; width:500px\" title=\"Olimpiade Tokyo 2020\" /></p>\r\n\r\n<p>Sebagaimana diketahui, DBON merupakan hasil karya pemerintah pusat, pemerintah daerah, dan organisasi masyarakat, terutama perguruan tinggi dan tim DPON untuk menyusun arah pembangunan olahraga di masa depan. Rencana ini sudah dilegitimasi dalam Peraturan Presiden (Perpres) Nomor 86 Tahun 2021.</p>\r\n\r\n<p>Melalui sambutannya, PLT Sesmenpora, Jonni Madrizal, memaparkan gambaran umum terkait DBON ini. Namun tentunya, ini semua harus berjalan konsisten dengan dukungan semua pihak.</p>\r\n\r\n<p>&ldquo;Saya yakin kalau kita bersama-sama kerja sama saling berkontribusi untuk mencapai target tersebut bisa terpenuhi. Tapi langkah-langkah harus secara konsisten. Sambil kita evaluasi,&rdquo; katanya.</p>\r\n\r\n<p>Adapun gambarannya, pertama, terkait target, bukan cuma sebagai 5 besar dalam ajang olahraga terbesar di dunia Olimpiade dan Paralimpiade. Ada juga target lain seperti pada 2045 nanti, 70 persen masyarakat aktif berolahraga, juga masyarakat bugar sebesar 60 persen.</p>\r\n\r\n<p>Tiga target itu merupakan sasaran utama dari DBON ini. Ada juga target perantara, yakni juara di ajang seperti SEA Games, Asian Games, hingga Para Games setiap tahunnya. Adapun model pembinaan dari DBON ini, yaitu pemerintah bekerja sama dengan perguruan tinggi untuk menciptakan atlet berprestasi. Bentuknya berupa latihan sebanyak minimal 10 ribu jam atau butuh waktu 8-10 tahun.</p>\r\n\r\n<p>&ldquo;Ini project jangka panjang yang artinya butuh pembinaan dari jauh-jauh hari. Tidak bisa ujug-ujug mengharapkan juara satu tahun berikutnya,&rdquo; lanjut Jonni.</p>\r\n\r\n<p>Kembali ke perhelatan Olimpiade dan Paralimpiade, DBON ini akan menetapkan target terdekat untuk mendulang medali emas pada 2032. Medali itu akan datang dari 14 cabor unggulan Olimpiade dan 15 cabor Paralimpiade.</p>\r\n', '2021-12-22', 1, 1),
(26, 'Kemenpora Sebut Esports Cabang Olahraga dengan Potensi Sangat Besar', 4, 'esport.jpg', '<p><strong>JAKARTA</strong> -- Ketua Penyelenggara Piala Presiden Esports 2021 Rangga Danu Prasetyo mengunjungi EVOS Integrated Training Facility yang merupakan markas klub esports profesional EVOS, di Jakarta Selatan, Rabu (1/12).<br />\r\n&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp;Rangga tidak sendiri, dia bersama Staf Khusus Bidang Kreativitas dan Inovasi Kaum Milenial Kementerian Pemuda dan Olahraga&nbsp;Alia Noorayu Laksono dan Tenaga Ahli Utama Kedeputian II Kantor Staf Presiden (KSP) Hageng Nugroho. Kunjungan dilakukan sebagai bagian dari upaya meningkatkan ekosistem esports nasional, seiring bergulirnya Turnamen Piala Presiden Esports (PPE) 2021.<br />\r\n<br />\r\nKedatangan mereka diterima oleh Co-founder &amp; Managing Director EVOS Esports Hartman Harris.&nbsp; &quot;Kunjungan kami ke EVOS ini merupakan bagian dari dukungan kepada ekosistem esports, khususnya melalui Piala Presiden Esports 2021.&quot;</p>\r\n\r\n<p>&quot;Kemenpora dalam hal ini melihat esports sebagai cabang olahraga dengan potensi yang sangat besar. Karena itu harus dibimbing dan digandeng, agar para atlet dan unsur-unsur lain di sekelilingnya dapat terdukung dan optimal meraih prestasi membanggakan,&rdquo; ujar Alia Laksono.&nbsp;</p>\r\n\r\n<p>Menurut Alia, perkembangan esports sangat signifikan beberapa tahun belakangan. Bahkan, telah membuka banyak peluang bagi generasi muda untuk berkembang sesuai dengan bakat dan kemampuan yang ada.</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;&quot;Untuk anak-anak muda yang punya interest menjadi atlet esports, bisa dibimbing dan dibina melalui teman-teman di PBESI, juga EVOS dan klub-klub lain bisa membantu anak muda Indonesia untuk mendapatkan training dan manajemen agar optimal sebagai atlet.&quot;<br />\r\n&nbsp; &nbsp; &nbsp;&quot;Kami berharap dunia esports ini bisa menjadi opsi baru bagi teman-teman muda mengharumkan nama bangsa,&quot; ucapnya. Alia Laksono mengapresiasi para pemain EVOS yang dinilainya memiliki mentalitas dan attitude sebagai atlet profesional. Menurutnya, atlet esports juga harus disiplin dalam menjalani pola latihan fisik yang baik sehingga memiliki kebugaran dan stamina yang memadai, sama seperti atlet olahraga konvensional lainnya.</p>\r\n\r\n<p><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Sementara itu, Co-founder &amp; Managing Director EVOS Esports Hartman Harris menyatakan merasa terhormat atas kunjungan Alia dan perwakilan pemerintah lainnya. &ldquo;Tentu sebuah kebanggaan bagi kami bahwa pemerintah memberikan perhatian khusus untuk atlet-atlet esports.&quot; &quot;Ini selaras dengan nilai-nilai dan keyakinan kami di EVOS dalam mengembangkan talenta-talenta yang kami miliki,&quot; ucapnya. Sementara itu, Ketua Penyelenggara PPE 2021 Rangga Danu Prasetyo menyatakan optimismenya ajang Piala Presiden Esports menjadi batu lompatan penting bagi pengembangan talenta-talenta atlet esports di semua level.<br />\r\n&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&ldquo;Kolaborasi dengan berbagai klub esports profesional sangat terbuka lebar. Piala Presiden Esports mendukung sepenuhnya pengembangan ekosistem esports di level klub, agar makin berkembang dan bersaing dengan klub esports mancanegara,&rdquo; katanya. Menurutnya, pelaksanaan Piala Presiden Esports 2021 telah menyelesaikan babak kualifikasi tertutup dan akan segera memasuki babak kualifikasi terbuka, sebelum akhirnya masuk ke babak grand final di Nusa Dua, Bali, pada Desember mendatang. Lebih kurang 130 ribu atlet&nbsp;esports dari berbagai daerah berpartisipasi di ajang kali ini.</p>\r\n', '2021-12-22', 1, 0),
(27, '2 Alasan Daihatsu Klaim All New Xenia Andal di Tanjakan  ', 8, '2-alasan-daihatsu-klaim-all-new-xenia-andal-di-tanjakan-JxjQQd0RPx.jpg', '<p>JAKARTA - All New Xenia generasi ketiga diklaim Daihatsu lebih kapabel melibas jalanan menanjak.</p>\r\n\r\n<p>Hal ini dibuktikan dengan melakukan test drive bersama awak media di Kebumen, Jawa Tengah pada Rabu 1 Desember 2021.</p>\r\n\r\n<p>Saat menjajal test drive, MNC Portal merasakan jika low MPV ini kuat saat jalan menanjak sampai kemiringan 18 derajat.</p>\r\n\r\n<p>Bambang Supriadi, Technical Service Division Head PT Astra Daihatsu menjelaskan, kedua alasan mobil terbaru Xenia ini lebih andal di jalanan menanjak.</p>\r\n\r\n<p>&quot;Mayoritas konsumen ketika dalam posisi tanjakan overtake dari pedal gasnya itu rata-rata 20 sampai 40%. Ini yang kita adjust di komputer ECU kita untuk dapat memberikan torsi yang maksimal.&quot;</p>\r\n\r\n<p>&quot;Kita bisa mencari tahu bagaimana kebiasaan dan tipikal dari pengemudi ketika sedang melibas ditanjakan. Melalui studi dan uji coba seperti apa kebiasaannya dan kemudian kita impementasikan ditanjakan. Semua itu menghasikan progam ECU yang pas terkait dengan tanjakan&quot; terang Bambang.</p>\r\n\r\n<p>Selain itu, pihak Daihatsu memilih gear ration yang lebih kecil sehingga bisa meningkatkan akselerasi lebih cepat dan rpm yang lebih rendah.</p>\r\n\r\n<p>&quot;Makanya hal ini membuat akselerasi dan konsumsi bahan bakar serta kemampuan unit di tanjakan bisa diandalkan,&quot; tegas Bambang</p>\r\n', '2021-12-24', 7, 1),
(28, 'BMW juluki i7 sebagai sedan listrik mewah pertama di dunia', 8, 'WhatsApp Image 2021-12-24 at 08.58.51.jpeg', '<p>Jakarta (ANTARA) -</p>\r\n\r\n<p>Produsen otomotif asal Jerman, BMW resmi membagikan gambar kendaraan elektrik yang mereka sebut sebagai sedan premium pertama di dunia yang sepenuhnya listrik, yakni BMW i7 2023 yang baru saja melakukan pengujian di Arjeplog, Swedia, dengan berbagai balutan stiker untuk penyamaran.<br />\r\n<br />\r\n<em>Statement&nbsp;</em>yang begitu percaya diri ini membuat BMW yakin dengan kemampuan kendaraan elektrik penuh ini untuk menjadi penantang dari Mercedes-Benz EQS yang sudah dijual setelah premier pada April 2021. Meski begitu, banyak yang berpendapat bahwasanya EQS bukan lah sedan karena terlihat dari palka belakang yang dimilikinya.<br />\r\n<br />\r\nSelain Mercedes-Benz EQS yang digadang-gadang akan menjadi penantangnya, Audi s-tron GT dan Lucid Air juga merupakan kendaraan listrik berbasis sedan. Namun, lagi-lagi BMW dibenarkan bahwa mereka sedan yang memiliki panjangnya di bawah 5 meter yang membuat mereka lebih dekat dengan trio Mercedes-Benz EQE, Audi A6 e-tron atau BMW i5 yang lebih kecil.<br />\r\n<br />\r\nDikutip dari CarsCoops, Kamis, BMW mungkin benar tentang i7 sebagai &quot;sedan mewah&nbsp;<em>all-electric</em>&nbsp;pertama di dunia&quot;, seperti halnya Mercedes-Benz ketika mereka mengatakan EQS mereka adalah &quot;kendaraan listrik pertama di kelas mewah&quot;.<br />\r\n<br />\r\nKembali ke prototipe, kami telah melihatnya beberapa kali, jadi tidak ada yang mengejutkan. Kamuflase hitam dengan pola putih tidak dapat menyembunyikan proporsi tiga kotak tradisional dengan kap mesin yang panjang dan jarak sumbu roda yang luas &ndash; fitur yang akan dimiliki oleh BMW Seri 7 berikutnya.<br />\r\n<br />\r\nDalam hal ini, mengenai desain yang mendetil bisa dilihat dari lampu depan yang diposisikan lebih rendah dengan gril yang besar. Bumper dari mobil ini tampaknya akan memiliki&nbsp;<em>intake&nbsp;</em>samping yang tipis sementara kap mesin&nbsp;<em>clamshell&nbsp;</em>dan profilnya lebih terpahat daripada Seri 7 saat ini sambil mempertahankan elemen khas seperti kekusutan Hoffmeister di pilar C.<br />\r\n<br />\r\nDalam hal ini, BMW telah banyak menghabiskan waktu untuk melakukan pengujian di jalan yang penuh dengan salju dari pusat tes musim dingin BMW Group di Arjeplog, Swedia yang hanya beberapa kilometer jauhnya dari Lingkaran Arktik.<br />\r\n<br />\r\nDi sana, para insinyur menguji sistem penggerak, suspensi, kemudi, dan pengereman, serta dinamika berkendara dan stabilitas kendaraan. Dalam cita rasa khas BMW, tujuannya adalah &quot;untuk meningkatkan keseimbangan antara&nbsp;<em>sporty&nbsp;</em>dan kenyamanan berkendara&quot;.<br />\r\n<br />\r\nTentu saja, setiap varian&nbsp;<em>powertrain&nbsp;</em>dari 7-Series/i7 akan menampilkan pengaturan yang berbeda. i7 akan menggunakan teknologi BMW Drive generasi kelima yang memulai debutnya di iX.<br />\r\n<br />\r\nDalam hal ini, perusahaan tidak mengungkapkan spesifikasi&nbsp;<em>powertrain&nbsp;</em>listrik atau baterai tegangan tinggi, selain konsumsi energi WLTP gabungan yang 22,5-19,5 kWh/100 km berdasarkan perkiraan saat ini.<br />\r\n<br />\r\nSebagai perbandingan, Mercedes-Benz EQS 450+ memiliki konsumsi energi yang lebih rendah yaitu 19,8 &ndash; 15,8 kWh/100 km tetapi juga tampaknya memiliki bentuk yang lebih aerodinamis. Perusahaan Bavaria mengkonfirmasi bahwa andalan listrik mereka akan diluncurkan sekitar tahun 2022.</p>\r\n', '2021-12-24', 7, 0),
(29, 'Dua Raksasa Otomotif AS Ini Berebut Posisi Ketiga Jualan EV', 8, '010986400-1637251987-830-556.jpg', '<p>Menit.com - Dua raksasa otomotif AS, General Motors Co dan Ford Motor Co kembali bersaing dalam menjual lebih banyak kendaraan listrik (EV) pada 2025. Kedua perusahaan asal Detroit, Amerika Serikat (AS) ini berlomba untuk menduduki posisi ketiga dalam pasar kendaraan jenis ini, setelah Tesla di peringkat pertama dan Volkswagen Group kedua.</p>\r\n\r\n<p>Pada Rabu (1/12), GM mengumumkan rencana untuk membentuk usaha bersama dengan POSCO dari Korea Selatan (Korsel) dalam membangun pabrik bahan katoda baterai di Amerika Utara pada 2024. Kesepakatan itu adalah bagian dari rencana jangka panjang pembuat mobil untuk secara hati-hati membangun mesin EV eksklusif yang terintegrasi secara vertikal, yang sepenuhnya berlangsung ketika biaya turun dan permintaan tumbuh di paruh kedua pada 2020-an. &ldquo;Tujuan kami adalah kepemimpinan pasar EV,&rdquo; ujar wakil presiden eksekutif GM, Doug Parks.&nbsp;</p>\r\n\r\n<p>Sementara itu, Ford akan menguraikan strategi EV yang sangat berbeda. Perusahaan otomotif ini berencana untuk segera meluncurkan beberapa model volume tinggi seperti Mustang Mach-E dan F-150 Lightning, menggunakan kendaraan pembakaran yang dimodifikasi.&nbsp;</p>\r\n\r\n<p>Ford akan menindaklanjuti pada pertengahan dekade ini dengan portofolio SUV dan truk listrik yang lebih luas, yang dirancang dari bawah ke atas untuk dijalankan dengan baterai. CEO Jim Farley mengatakan telah meminta untuk menambah lebih banyak kapasitas produksi EV, dibanding rencana awal yang diumumkan, serta perlu asan kapasitas F-150 listrik di Dearborn, Michigan.</p>\r\n\r\n<p>Terlepas dari yang dilakukan oleh GM dan Ford, AutoForecast Solutions (AFS) mengatakan kedua perusahaan pembuat kendaraan ini diperkirakan masih jauh tertinggal di belakang Tesla dan Group VW hingga 2028. Selain itu, masing-masing juga harus menghadapi pesaing kejutan untuk menduduki tempat ketiga dalam pasar EV, diantaranya adalah dengan Stellantis, perusahaan Eropa yang dibentuk tahun lalu oleh penggabungan pembuat mobil Prancis PSA dan pembuat mobil Italia-Amerika, Fiat Chrysler.</p>\r\n\r\n<p>AFS memperkirakan output global Tesla akan mencapai hampir 1,5 juta pada 2025, dengan pertumbuhan lebih lanjut tergantung di mana dan bagaimana perusahaan memperluas jejak produksinya. Perkiraan juga menyebut Grup VW untuk mencapai 1,45 juta dari produksi EV pada 2025 dan naik menjadi 1,9 juta pada 2028.</p>\r\n\r\n<p>Produksi EV global Ford diperkirakan akan mencapai 695.000 pada 2025 dan 1,14 juta pada 2028. Angka EV GM terlihat jauh lebih tipis, yaitu 494.000 pada 2025 dan 624.000 pada 2028. Angka tersebut tidak termasuk produksi dari usaha patungan SAIC-GM-Wuling di Cina, yang memproduksi EV di bawah merek Baojun dan Wuling dan di mana GM merupakan pemegang saham minoritas.</p>\r\n\r\n<p>Menurut AFS, Stellantis bertujuan untuk membangun 948.000 EV pada 2025 dan 1,2 juta pada 2028, yang akan menempatkan perusahaan di tempat ketiga, di depan Ford.</p>\r\n\r\n<p>Strategi Ford berbeda dari GM dalam hal lain. Sementara kedua perusahaan berencana untuk membangun EV di Amerika Serikat (AS), Kanada, Meksiko dan Cina.&nbsp;Ford juga akan memproduksi sumber di Jerman, Turki dan Spanyol. GM akan mendapatkan kendaraan listrik dari sembilan pabrik pada 2028, Ford dari 12, menurut data AFS.</p>\r\n\r\n<p>Kedua perusahaan akan sangat menggemparkan merek premium AS mereka. Cadillac GM akan menawarkan Lyriq, Symboliq, Celestiq dan Escalade EV, serta SUV kompak, pada 2025. Lincoln Ford akan melawan versi listrik dari Corsair, Aviator dan Navigator, bersama dengan kendaraan model pickup listrik.</p>\r\n', '2021-12-24', 7, 0),
(30, '\"Prediksi Tren Mobil dan Fokus Honda pada 2022\"', 8, '619fc28ca21fa.jpg', '<p>JAKARTA, KOMPAS.com - PT Honda Prospect Motor (HPM) memprediksi pasar otomotif pada 2022 akan lebih baik. Hal ini terindikasi dari penjualan 7.005 unit mobil selama November 2021. Business Innovation and Sales &amp; Marketing Director PT HPM Yusak Billy mengatakan, pihaknya akan terus optimis meraih tren penjualan yang positif hingga akhir tahun &ldquo;Saat ini penjualan Honda masih lebih tinggi 17 persen, dibandingkan periode yang sama tahun lalu. Kami percaya trend positif ini masih akan bertahan sampai akhir tahun,&rdquo; ucap Billy saat dihubungi Kompas.com, Kamis (23/12/2021).</p>\r\n\r\n<p>Sementara ketika ditanya soal tren pasar di tahun depan, secara model Billy memperkirakan segmen mobil tujuh penumpang masih akan digemari. &ldquo;Untuk tahun depan, kami melihat segmen SUV termasuk LSUV dan first time buyer masih akan terus menjadi model yang digemari oleh konsumen,&rdquo; kata Billy. Lebih lanjut lagi, Billy menjelaskan fokus Honda di tahun depan. Ia mengatakan, pada Januari 2022 pihaknya akan meningkatkan produksi dan pengiriman mobil kepada konsumen.</p>\r\n\r\n<p>Menyoal target penjualan Honda di tahun 2022, Billy mengatakan, pihaknya masih sangat tergantung pada kondisi pandemi dan pasokan komponen. &ldquo;Situasi saat ini menunjukkan perkembangan yang positif untuk kedua kondisi tersebut sehingga kami optimis penjualan tahun depan akan lebih baik,&rdquo; kata dia.<br />\r\n<br />\r\n&nbsp;</p>\r\n', '2021-12-24', 7, 0),
(31, 'Toyota Indonesia Ekspor Mobil Hybrid Mulai 2022, Simak Strategi Bisnisnya', 8, '971977_720.jpg', '<p>Presiden Direktur Toyota&nbsp;Motor Manufacturing Indonesia (TMMIN) Warih Andang Tjahjono menyatakan Toyota Indonesia akan mengekspor mobil listrik hybrid (hybrid electric vehicle/HEV) mulai 2022.</p>\r\n\r\n<p>&quot;Kami mulai persiapan model HEV produksi lokal di tahun 2022. Kami yakin ekspor mobil akan menjadi jembatan rantai pasokan ke pasar global,&quot; kata Warih yang dikutip hari ini, Kamis, 23 Desember 2021.</p>\r\n\r\n<p>Meski begitu, Warih&nbsp;tak menjelaskan mobil Toyota&nbsp;model&nbsp;hybrid&nbsp;yang mana yang akan diekspor. Negara tujuan ekspor mobil Toyota&nbsp;hybrid&nbsp;juga tak disinggung.&nbsp;&nbsp;</p>\r\n\r\n<p>Warih menjelaskan, TMMIN menggunakan strategi multi-pathway, yakni tidak terpaku pada satu jenis mobil, melainkan memproduksi berbagai jenis mobil sesuai kebutuhan pasar.</p>\r\n\r\n<p>Toyota&nbsp;memproduksi varian mobil listrik seperti mobil hybrid&nbsp;(HEV), Battery Electric Vehicle (BEV), Plug-In Hybrid Vehicle (PHEV), juga mobil bermesin bakar internal&nbsp;(ICE). Namun, mobil ICE atau mesin konvensional ini dibekali&nbsp;teknologi rendah emisi, seperti Electronic Fuel Injection (EFI) dan Dual Variable Valve Timing-Intelligent (Dual VVT-I).</p>\r\n\r\n<p>&quot;Agar pelanggan Toyota bisa memilih berdasarkan kebutuhan mereka dan berkontribusi dalam pengurangan emisi,&quot; ucap bos Toyota&nbsp;TMMIN ini.</p>\r\n\r\n<p>&nbsp;</p>\r\n', '2021-12-24', 7, 1);

